INSERT INTO `t_template` VALUES ('7', 'Controller层代码', 'package ${entity.currentFileFullPackage};\r\n\r\nimport java.util.Date;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\nimport javax.annotation.Resource;\r\nimport javax.servlet.http.HttpServletRequest;\r\nimport javax.servlet.http.HttpSession;\r\nimport javax.validation.Valid;\r\n\r\nimport org.springframework.ui.Model;\r\nimport org.springframework.beans.factory.annotation.Autowired;\r\nimport org.springframework.stereotype.Controller;\r\nimport org.springframework.validation.BindingResult;\r\nimport org.springframework.validation.Errors;\r\nimport org.springframework.validation.FieldError;\r\nimport org.springframework.validation.Validator;\r\nimport org.springframework.web.bind.WebDataBinder;\r\nimport org.springframework.web.bind.annotation.InitBinder;\r\nimport org.springframework.web.bind.annotation.ModelAttribute;\r\nimport org.springframework.web.bind.annotation.PathVariable;\r\nimport org.springframework.web.bind.annotation.RequestMapping;\r\nimport org.springframework.web.bind.annotation.RequestMethod;\r\nimport org.springframework.web.bind.annotation.RequestParam;\r\nimport org.springframework.web.bind.annotation.ResponseBody;\r\n\r\nimport com.novots.itsm.common.entity.enumtype.YesNo;\r\nimport com.novots.itsm.util.bean.CheckResult;\r\n\r\n\r\nimport com.novots.itsm.util.web.JsonResult;\r\nimport com.novots.itsm.common.controller.BaseController;\r\nimport com.novots.itsm.util.web.JsonResult;\r\nimport com.novots.itsm.history.entity.HistoryRecord;\r\n\r\nimport ${entity.fullClassName};\r\n\r\n<c:forEach items=\"${entity.children}\" var=\"child\">\r\nimport ${child.fullClassName};\r\n</c:forEach>\r\n\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.dataType==6}\">\r\nimport ${p.complexEntity.fullClassName};\r\n</c:if>\r\n</c:forEach>\r\n\r\nimport com.novots.itsm.util.Md5Util;\r\nimport com.novots.itsm.util.web.Page;\r\n\r\n<c:if test=\"${not empty entity.parent}\">\r\nimport ${entity.parent.fullClassName};\r\n</c:if>\r\n\r\n\r\n/**\r\n * ${entity.name}Controller \r\n * auto created by pc\r\n * @author codeMaker 2.0\r\n * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n */\r\n@Controller\r\n@RequestMapping(\"/admin/${entity.lowerClassName}\")\r\npublic class ${entity.className}Controller  extends BaseController {\r\n\r\n	<c:if test=\"${not empty entity.parent}\">\r\n		@Resource\r\n		private ${entity.parent.className}Controller ${entity.parent.lowerClassName}Controller;\r\n	</c:if>\r\n\r\n    @Resource\r\n    private I${entity.className}Service ${entity.lowerClassName}Service;\r\n    \r\n    @InitBinder\r\n	public void initPropEditor(WebDataBinder binder){\r\n    	    //注册属性编辑器\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.initPropEditor(binder);\r\n</c:if>\r\n	    #{INIT_PROP_EDITOR}\r\n	}\r\n    \r\n    //复杂关联关系的Service\r\n    #{COMPLEX_SERVICES_DEFINE}\r\n    \r\n\r\n<c:choose>\r\n	<c:when test=\"${entity.manageKeySupport==1}\">\r\n		@ModelAttribute(value=\"${entity.lowerClassName}\")\r\n		public ${entity.className} initModelAttribute(@RequestParam(value = \"manageKey\", required = false) String manageKey, Model model) {\r\n		${entity.className} ${entity.lowerClassName} = null;\r\n		if (manageKey == null ||manageKey.equals(\"\")) {\r\n			${entity.lowerClassName} = new ${entity.className}();\r\n		} else {\r\n			List<${entity.className}> list =  ${entity.lowerClassName}Service.getByProperty(${entity.className}.class,\"manageKey\", manageKey);\r\n			if(list.size()>0){\r\n				${entity.lowerClassName} =list.get(0);\r\n			}\r\n			if (${entity.lowerClassName}==null) {\r\n				${entity.lowerClassName}=new ${entity.className}();\r\n			}\r\n		}\r\n		return ${entity.lowerClassName};\r\n	}\r\n	</c:when>\r\n	<c:otherwise>\r\n	@ModelAttribute\r\n    public ${entity.className} initModelAttribute(@RequestParam(value=\"${entity.idColumnName}\",required=false) ${empty entity.idColumnProp ? \'Integer\':entity.idColumnProp.dataTypeName} ${entity.idColumnName}){\r\n    	${entity.className} ${entity.lowerClassName} = null;\r\n    	if(${entity.idColumnName}==null || ${entity.idColumnName}.equals(\"\")){\r\n    		${entity.lowerClassName} = new ${entity.className}();\r\n    	}else{\r\n    		${entity.lowerClassName} = ${entity.lowerClassName}Service.getById(${entity.className}.class, ${entity.idColumnName});\r\n    	}\r\n    	return ${entity.lowerClassName};\r\n    }\r\n	</c:otherwise>\r\n</c:choose>\r\n\r\n    \r\n\r\n\r\n\r\n\r\n\r\n    \r\n    /**\r\n     * ${entity.name}列表\r\n     * @param request\r\n     * @return\r\n     * @author pc\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/list\")\r\n    public void list(HttpServletRequest request,${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n\r\n        Page<${entity.className}> ${entity.lowerClassName}Page = ${entity.lowerClassName}Service.getListPage(${entity.lowerClassName},pageInfo);\r\n        request.setAttribute(\"pageBean\", ${entity.lowerClassName}Page);\r\n        \r\n	     \r\n		listFormParam(request,${entity.lowerClassName},pageInfo);\r\n    }\r\n    \r\n	public void listFormParam(HttpServletRequest request,${entity.className} ${entity.lowerClassName},Page pageInfo){\r\n		//复杂关联关系数据准备\r\n    	#{COMPLEX_LIST_PREPARE}\r\n	\r\n		<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.listFormParam(request,${entity.lowerClassName},pageInfo);\r\n	    </c:if>\r\n	}\r\n	\r\n    /**\r\n     * 添加${entity.name}页面\r\n     * @param request\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/add\",method=RequestMethod.GET)\r\n    public void addUI(HttpServletRequest request,@RequestParam(value = \"callBack\",required = false)String callBack) {\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.addUI(request,callBack);\r\n</c:if>\r\n    	//复杂关联关系数据准备\r\n        #{COMPLEX_LIST_PREPARE}\r\n	clearTempDataList(request.getSession(),\"${entity.lowerClassName}<c:forEach items=\"${entity.parents}\" var=\"p\">,${p.lowerClassName}</c:forEach>\");\r\n		request.setAttribute(\"callBack\", callBack);\r\n    }\r\n    \r\n    /**\r\n     * 添加ENTITY_REALY_NAME\r\n     * @param ${entity.lowerClassName}\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/add\",method=RequestMethod.POST)\r\n    @ResponseBody\r\n    public JsonResult add(HttpServletRequest request,@RequestParam(\"rel\") String rel,\r\n	@Valid @ModelAttribute(\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},\r\n	@RequestParam(value = \"callBack\",required = false)String callBack,\r\n	#{MANY_TO_MANY_IDS}\r\n	BindingResult result) {\r\n    	JsonResult jsonResult = new JsonResult();\r\n    	//if(!validate(jsonResult,result)){\r\n    	//	return jsonResult;\r\n    	//}\r\n    	//默认系统时间类型保存\r\n    	#{DATATYPE_DEFAULT_VALUE_SAVE}\r\n	\r\n		#{ONE_TO_MANY_VALUE_SAVE_ADD}\r\n	    //保存多对多关联关系\r\n		#{SAVE_MANY_TO_MANY_RELATION_ADD}\r\n	<c:if test=\"${not empty entity.parent}\">\r\n		//调用父类添加方法，以保存多对多关系\r\n		#{INVOKE_PARENT_ADD}\r\n	</c:if>\r\n	\r\n	<c:if test=\"${entity.manageKeySupport==1 and empty entity.parent}\">\r\n	${entity.lowerClassName}.setManageKey(Md5Util.getManageKey());\r\n	</c:if>\r\n    	${entity.lowerClassName}Service.add(${entity.lowerClassName});\r\n    	jsonResult.setNavTabId(rel);\r\n		jsonResult.setCallBackFn(callBack);\r\n    	jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.add.success\"));\r\n        return jsonResult;\r\n    }\r\n    \r\n    /**\r\n     * 修改${entity.name}页面\r\n     * @param uid\r\n     * @param request\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/update\",method=RequestMethod.GET)\r\n	public void updateUI(${entity.className} ${entity.lowerClassName},HttpServletRequest request) {\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.updateUI(${entity.lowerClassName},request);\r\n</c:if>\r\n	\r\n	request.setAttribute(\"tempBean\", ${entity.lowerClassName});\r\n    	//复杂关联关系数据准备\r\n        #{COMPLEX_LIST_PREPARE}\r\n	\r\n		clearTempDataList(request.getSession(),\"${entity.lowerClassName}<c:forEach items=\"${entity.parents}\" var=\"p\">,${p.lowerClassName}</c:forEach>\");\r\n    }\r\n\r\n\r\n    \r\n    /**\r\n     * 修改${entity.name}\r\n     * @param ${entity.lowerClassName}\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/update\",method=RequestMethod.POST)\r\n    @ResponseBody\r\n    public JsonResult update(HttpServletRequest request,@RequestParam(\"rel\") String rel,\r\n	@ModelAttribute ${entity.className} ${entity.lowerClassName},\r\n	#{MANY_TO_MANY_IDS}\r\n	BindingResult result) {\r\n    	JsonResult jsonResult = new JsonResult();\r\n    	if(!validate(jsonResult,result)){\r\n    		return jsonResult;\r\n    	}\r\n		\r\n	    //保存多对多关联关系\r\n		#{SAVE_MANY_TO_MANY_RELATION_UPDATE}\r\n	\r\n	<c:if test=\"${not empty entity.parent}\">\r\n		//调用父类修改方法，以保存多对多关系\r\n		#{INVOKE_PARENT_UPDATE}\r\n	</c:if>\r\n        ${entity.lowerClassName}Service.update(${entity.lowerClassName});\r\n	    jsonResult.setNavTabId(rel);\r\n        jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.update.success\"));\r\n        return jsonResult;\r\n    }\r\n	/**\r\n	* 查看${entity.name}页面\r\n     * @param uid\r\n     * @param request\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/detail\",method=RequestMethod.GET)\r\n	public void detail(${entity.className} ${entity.lowerClassName},HttpServletRequest request) {\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.detail(${entity.lowerClassName},request);\r\n</c:if>\r\n	\r\n	request.setAttribute(\"tempBean\", ${entity.lowerClassName});\r\n    	//复杂关联关系数据准备\r\n        #{COMPLEX_LIST_PREPARE}\r\n    }\r\n\r\n\r\n<c:if test=\"${entity.exportable==1}\">\r\n	/**\r\n	 * 导出${entity.name}\r\n	 * @param ${entity.lowerClassName}\r\n	 * @return \r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n	 */\r\n	@RequestMapping(value = \"/export\", method = RequestMethod.GET)\r\n	@ResponseBody\r\n	public JsonResult export(HttpServletRequest request,HttpServletResponse response,${entity.className} ${entity.lowerClassName},Page pageInfo,@RequestParam(value = \"forTemplate\",required = false) boolean forTemplate) {\r\n		if(forTemplate){//导出模板\r\n			${entity.lowerClassName}Service.export(response,Collections.EMPTY_LIST);\r\n		}else{//导出数据\r\n			pageInfo.setScopeDepts(departmentService.getCurrentScopeDepts(request.getSession()));\r\n			List<${entity.className}> ${entity.lowerClassName}List = ${entity.lowerClassName}Service.findAllList(${entity.lowerClassName}, pageInfo);\r\n			${entity.lowerClassName}Service.export(response,${entity.lowerClassName}List);\r\n		}\r\n		JsonResult jsonResult = new JsonResult();\r\n		jsonResult.setCallbackType(\"\");\r\n		jsonResult.setMessage(\"导出成功\");\r\n		return jsonResult;\r\n	}\r\n</c:if>\r\n	<c:if test=\"${entity.importable==1}\">\r\n	\r\n	/**\r\n	 * 导入页面\r\n	 * @param request\r\n	 */\r\n	@RequestMapping(value = \"/import\", method = RequestMethod.GET,params={\"import=1\"})\r\n	public void importUI(HttpServletRequest request) {\r\n		\r\n	}\r\n	/**\r\n	 * 下载导入模板\r\n	 * @param request\r\n	 */\r\n	@RequestMapping(value = \"/import\",params={\"downloadtemplate=1\"},method=RequestMethod.GET)\r\n	public String exportTemplate(HttpServletRequest request,HttpServletResponse response) {\r\n		socialSecurityService.export(response,Collections.EMPTY_LIST);\r\n		return null;\r\n	}\r\n\r\n    /**\r\n	 * 导入\r\n	 * @param archive\r\n	 * @return\r\n	 */\r\n		@RequestMapping(value = \"/import\",method=RequestMethod.POST,params={\"import=1\"})\r\n		@ResponseBody\r\n	public JsonResult importExcel(@RequestParam(value = \"archive\",required = false) String archive){\r\n		ImportResult result = null;\r\n		JsonResult jsonResult = new JsonResult();\r\n		jsonResult.setCallbackType(\"forward\");\r\n		jsonResult.setForwardUrl(\"socialSecurity/import.do?result=1\");\r\n		if(archive==null){\r\n			result = new ImportResult();\r\n			result.setMessage(\"文件上传失败\");\r\n			setAttribute(\"importResult\", result);\r\n			setSessionAttribute(\"importResult\", result);\r\n			\r\n			return jsonResult;\r\n		}\r\n		CommonFile filee = commonFileService.getEntityByProperty(CommonFile.class,\"manageKey\",archive);\r\n		if(filee == null){\r\n			result = new ImportResult();\r\n			result.setMessage(\"文件上传失败\");\r\n			setSessionAttribute(\"importResult\", result);\r\n\r\n			return jsonResult;\r\n		}\r\n		FileInputStream is = null;\r\n		File file = null;\r\n		try {\r\n			file = new File(filee.getFullName());\r\n			is = new FileInputStream(file);\r\n			result = ${entity.lowerClassName}Service.importExcel(is);\r\n			setSessionAttribute(\"importResult\",result);\r\n		}catch (Exception e) {\r\n			e.printStackTrace();\r\n		}finally{\r\n			if(is!=null){\r\n				try {\r\n					is.close();\r\n				} catch (Exception e2) {\r\n					e2.printStackTrace();\r\n				}\r\n			}\r\n			file.delete();\r\n			commonFileService.delete(filee);\r\n		}\r\n\r\n		return jsonResult;\r\n	}\r\n\r\n	\r\n	</c:if>\r\n    \r\n	\r\n	\r\n    /**\r\n     * 删除单个\r\n     * @param uid\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n     */\r\n    @RequestMapping(value=\"/delete\",method=RequestMethod.GET)\r\n    @ResponseBody\r\n    public JsonResult delete(HttpServletRequest request,@RequestParam(\"rel\") String rel,@ModelAttribute ${entity.className} ${entity.lowerClassName}) {\r\n        JsonResult jsonResult = new JsonResult();\r\n        jsonResult.setCallbackType(\"\");\r\n        try {\r\n        	${entity.lowerClassName}Service.delete(${entity.lowerClassName});\r\n		} catch (RuntimeException re) {\r\n			jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.inuse\"));\r\n			return jsonResult;\r\n		}catch (Exception e) {\r\n			jsonResult.setMessage(e.toString());\r\n			return jsonResult;\r\n		}\r\n        jsonResult.setNavTabId(rel);\r\n        jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.success\"));\r\n        return jsonResult;\r\n    }\r\n	\r\n	\r\n	@RequestMapping(value = \"/selectList\")\r\n	public void selectList(HttpServletRequest request, ${entity.className} ${entity.lowerClassName}, \r\n			Page pageInfo,@RequestParam(value = \"multiSelect\",required = false) boolean multiSelect,\r\n			@RequestParam(value = \"targetType\",required = false) String targetType\r\n			) {\r\n		\r\n	    Page<${entity.className}> ${entity.lowerClassName}Page = ${entity.lowerClassName}Service.getListPage(${entity.lowerClassName},pageInfo);\r\n        request.setAttribute(\"pageBean\", ${entity.lowerClassName}Page);\r\n	\r\n		#{COMPLEX_LIST_PREPARE}\r\n		\r\n		request.setAttribute(\"multiSelect\", multiSelect);\r\n		request.setAttribute(\"targetType\", targetType);\r\n	}\r\n	\r\n	/**\r\n	 * ajax加载下拉列表选择框数据\r\n	 * @param request\r\n	 * @param teacher\r\n	 * @param pageInfo\r\n	 * @return\r\n	 */\r\n	@RequestMapping(value = \"/selectList\",params = \"jsonType\")\r\n	@ResponseBody\r\n	public List<${entity.className}> selectListWithJson(HttpServletRequest request, ${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		List<${entity.className}> ${entity.lowerClassName}s = ${entity.lowerClassName}Service.findAll(${entity.className}.class);\r\n		return ${entity.lowerClassName}s;\r\n	}\r\n	\r\n	@RequestMapping(value = \"/selectList\",params = \"htmlType\")\r\n	public String selectListWithHtml(HttpServletRequest request,\r\n			@RequestParam(value = \"manageKey\",required = false)String[] manageKeys,\r\n			@RequestParam(value=\"from\",required = true)String from\r\n			){\r\n		List<${entity.className}> ${entity.lowerClassName}s = new ArrayList<${entity.className}>();\r\n		if(manageKeys!=null){\r\n			for(String manageKey : manageKeys){\r\n				${entity.className} temp_${entity.lowerClassName} = ${entity.lowerClassName}Service.getEntityByProperty(${entity.className}.class, \"manageKey\", manageKey);\r\n				if(temp_${entity.lowerClassName}!=null && !${entity.lowerClassName}s.contains(temp_${entity.lowerClassName})){\r\n					${entity.lowerClassName}s.add(temp_${entity.lowerClassName});\r\n				}\r\n			}\r\n		}\r\n		request.setAttribute(\"showCheckBox\",request.getParameter(\"showCheckBox\"));\r\n		request.setAttribute(\"keyName\",request.getParameter(\"keyName\"));\r\n		request.setAttribute(\"${entity.lowerClassName}s\",${entity.lowerClassName}s);\r\n		return \"admin/${entity.lowerClassName}/selectListForHtml\";\r\n	}\r\n	\r\n	\r\n	/**\r\n	 * 历史记录列表\r\n	 */\r\n	@RequestMapping(value = \"history\",params=\"list\")\r\n	public String historyList(HttpServletRequest request,${entity.className} ${entity.lowerClassName},Page pageInfo){\r\n		Page<HistoryRecord<${entity.className}>> ${entity.lowerClassName}Page = ${entity.lowerClassName}Service.getHistoryListPage(${entity.lowerClassName}, pageInfo);\r\n		request.setAttribute(\"pageBean\", ${entity.lowerClassName}Page);\r\n		request.setAttribute(\"manageKey\", ${entity.lowerClassName}.getManageKey());\r\n	    return \"admin/${entity.lowerClassName}/list-history\";\r\n	}\r\n	\r\n	/**\r\n	 * 查看历史记录\r\n	 */\r\n	@RequestMapping(value = \"history\",params=\"view\")\r\n	public String historyView(HttpServletRequest request,@RequestParam(value = \"version\")Integer version){\r\n		${entity.className} ${entity.lowerClassName} = ${entity.lowerClassName}Service.findByVersion(version);\r\n		${entity.className} pre${entity.className} = null;\r\n	while(--version>0 && pre${entity.className}==null){\r\n			pre${entity.className} = ${entity.lowerClassName}Service.findByVersion(version);\r\n		}\r\n	if(pre${entity.className}!=null && pre${entity.className}.getManageKey().equals(${entity.lowerClassName}.getManageKey())){\r\n		Map<String,CheckResult> checkResult = BeanPropUtil.propValueCheck(pre${entity.className}, ${entity.lowerClassName});\r\n		request.setAttribute(\"checkResult\", checkResult);\r\n	}\r\n		request.setAttribute(\"tempBean\", ${entity.lowerClassName});\r\n		return \"admin/${entity.lowerClassName}/detail\";\r\n	}\r\n	\r\n	\r\n   \r\n    /**\r\n     * 删除多个\r\n     * @param uids\r\n     * @return\r\n     * <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n   \r\n    @RequestMapping(value=\"/delete\",method=RequestMethod.POST)\r\n    @ResponseBody\r\n    public JsonResult batchDelete(@RequestParam(\"ID_PROPERTY_NAMEs\") ID_PROPERTY_DATATYPENAME[] ID_PROPERTY_NAMEs) {\r\n    	${entity.className} ${entity.lowerClassName} = null;\r\n    	for(ID_PROPERTY_DATATYPENAME id : ID_PROPERTY_NAMEs){\r\n    		${entity.lowerClassName} = ${entity.lowerClassName}Service.loadById(${entity.className}.class, id);\r\n    		${entity.lowerClassName}Service.delete(${entity.lowerClassName});\r\n    	}\r\n    	JsonResult jsonResult = new JsonResult();\r\n    	jsonResult.setMessage(\"删除成功\");\r\n    	return jsonResult;\r\n    } \r\n    */\r\n    \r\n}', '1', '2');
INSERT INTO `t_template` VALUES ('8', 'Service接口代码', 'package ${entity.currentFileFullPackage};\r\n\r\nimport com.novots.itsm.util.web.Page;\r\nimport com.novots.itsm.common.service.IBaseService;\r\n\r\nimport java.text.SimpleDateFormat;\r\nimport java.util.ArrayList;\r\nimport java.util.Date;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\n\r\nimport com.novots.itsm.history.entity.HistoryRecord;\r\n\r\nimport ${entity.fullClassName};\r\n/**\r\n * ${entity.name}Service接口\r\n * @author \r\n *  <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n */\r\npublic interface I${entity.className}Service extends IBaseService<${entity.className}> {\r\n\r\n    /**\r\n     * 得到分页信息\r\n     * @param role\r\n     * @return\r\n     */\r\n    public Page<${entity.className}> getListPage(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	public List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n   \r\n    /**\r\n	* 导出${entity.name}信息\r\n     * @param role\r\n     * @return\r\n     */\r\n    public void export(HttpServletResponse response,List<${entity.className}> dataList);\r\n</c:if>\r\n	<c:if test=\"${entity.importable==1}\">\r\n	\r\n    /**\r\n	 * 导入\r\n	 * @param is\r\n	 * @return\r\n	 */\r\n	public ImportResult importExcel(FileInputStream is) throws Exception;\r\n	\r\n	\r\n	</c:if>\r\n	\r\n	public Page<HistoryRecord<${entity.className}>> getHistoryListPage(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n\r\n	/**\r\n	 * 按照版本查询\r\n	 * @param version\r\n	 * @return\r\n	 */\r\n	public ${entity.className} findByVersion(Integer version);\r\n	\r\n	\r\n}', '1', '2');
INSERT INTO `t_template` VALUES ('9', 'Service实现代码', 'package ${entity.currentFileFullPackage};\r\n\r\nimport java.util.List;\r\n\r\nimport javax.annotation.Resource;\r\nimport org.springframework.stereotype.Service;\r\n\r\n\r\nimport org.apache.poi.hssf.usermodel.HSSFCell;\r\nimport org.apache.poi.hssf.usermodel.HSSFRow;\r\nimport org.apache.poi.hssf.usermodel.HSSFSheet;\r\nimport org.apache.poi.hssf.usermodel.HSSFWorkbook;\r\n\r\nimport com.novots.itsm.util.web.Page;\r\nimport com.novots.itsm.common.service.IBaseService;\r\nimport ${entity.fullClassName};\r\nimport com.novots.itsm.history.entity.HistoryRecord;\r\n\r\n/**\r\n * @projectName catfish\r\n * @packageName ${entity.currentFileFullPackage}\r\n * @ClassName ${entity.className}ServiceImpl\r\n * @Description @${entity.name}Service接口实现\r\n * @author codeMaker 2.0\r\n * @date <fmt:formatDate value=\"${now}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n */\r\n@Service\r\npublic class ${entity.className}ServiceImpl extends BaseServiceImpl<${entity.className}> implements I${entity.className}Service {\r\n\r\n    @Resource\r\n    private I${entity.className}Dao ${entity.lowerClassName}Dao;\r\n    \r\n    @Override\r\n    public Page<${entity.className}> getListPage(${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n      	pageInfo.setTotalCount(${entity.lowerClassName}Dao.findListRowCount(${entity.lowerClassName}).intValue());\r\n		pageInfo.setOrderField(pageInfo.getOrderField() == null ? \"id\": pageInfo.getOrderField());\r\n		pageInfo.setOrderDirection(pageInfo.getOrderDirection() == null ? \"asc\": pageInfo.getOrderDirection());\r\n		pageInfo.setList(${entity.lowerClassName}Dao.findListPage(${entity.lowerClassName},pageInfo));\r\n        return pageInfo;\r\n    }\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	@Override\r\n	public List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n		return ${entity.lowerClassName}Dao.findAllList(${entity.lowerClassName},pageInfo);\r\n	}\r\n	@Override\r\n	public void export(HttpServletResponse response,List<${entity.className}> dataList) {\r\n	// 格式化后的${entity.name}LIST\r\n		SheetInfo sheetInfo = get${entity.className}SheetInfo();\r\n		sheetInfo.setData(complexObjectDateToMapList(dataList, sheetInfo));\r\n\r\n		List<SheetInfo> export${entity.className}List = new ArrayList<SheetInfo>();\r\n		export${entity.className}List.add(sheetInfo);\r\n		ExportPOIUtil.exportExcel(response, export${entity.className}List, sheetInfo.getSheetName());\r\n	}\r\n	// 设置格式\r\n	private SheetInfo get${entity.className}SheetInfo() {\r\n	StringBuffer title= new StringBuffer(\"${entity.name}信息\");\r\n		title.append(new SimpleDateFormat(\"yyyyMMdd\").format(new Date()));\r\n	\r\n		SheetInfo sheetInfo = new SheetInfo(title.toString());\r\n	sheetInfo.setHeaders(new String[] {<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">\"${p.name}\"${not s.last ? \',\':\'\'}</c:forEach>});\r\n		sheetInfo.setPropertys(createPropsWithHeaders(sheetInfo.getHeaders()));\r\n	sheetInfo.setColwidth(new int[] {<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">15${not s.last ? \',\':\'\'}</c:forEach>});\r\n		sheetInfo.setCellTypes(new int[] { \r\n	<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">HSSFCell.CELL_TYPE_STRING, HSSFCell.CELL_TYPE_STRING${not s.last ? \',\':\'\'}</c:forEach>});\r\n		sheetInfo.setDefaultCell(sheetInfo.getHeaders().length, HSSFCell.CELL_TYPE_STRING);\r\n		sheetInfo.setDefaultCell(sheetInfo.getHeaders().length, HSSFCell.CELL_TYPE_STRING);\r\n		sheetInfo.setDefaultHeadSytle(sheetInfo.getHeaders().length,SheetInfo.CELL_STYLE_ORANGE);\r\n		sheetInfo.setDefaultOper(sheetInfo.getHeaders().length, SheetInfo.OPER_NO);\r\n		return sheetInfo;\r\n	}\r\n	// 根据headers创建properties\r\n	private String[] createPropsWithHeaders(String[] headers) {\r\n		List<String> props = new ArrayList<String>();\r\n		int i = 0;\r\n		for (String header : headers) {\r\n			props.add(\"prop_\" + (i++));\r\n		}\r\n		String[] res = new String[headers.length];\r\n		i = 0;\r\n		for (Object obj : props.toArray()) {\r\n			res[i++] = (String) obj;\r\n		}\r\n		return res;\r\n	}\r\n	//${entity.name}信息数据整理\r\n	private List<Map<String, String>> complexObjectDateToMapList(List<${entity.className}> data, SheetInfo sheetInfo) {\r\n		List<Map<String, String>> result = new ArrayList<Map<String, String>>();\r\n		Map<String, String> rowMap = null;\r\n		int index = 1;\r\n	for (${entity.className} ${entity.lowerClassName} : data) {\r\n			try {\r\n				rowMap = new HashMap<String, String>();\r\n				\r\n				rowMap.put(getPropNameByHeaderName(\"序号\", sheetInfo), index + \"\");\r\n	<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">\r\n		<c:choose>\r\n			<c:when test=\"${p.dataType==1 or p.dataType==2 or p.dataType==3}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), StringUtil.mildToString(${entity.lowerClassName},\"${p.upperCasePropName}\"));\r\n			</c:when>\r\n			<c:when test=\"${p.dataType==7}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), StringUtil.mildToString(${entity.lowerClassName},\"${p.upperCasePropName}\",\"Description\"));\r\n			</c:when>\r\n			<c:when test=\"${p.dataType==6}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), StringUtil.mildToString(${entity.lowerClassName},\"${p.upperCasePropName}\",\"${p.upperCaseValuePath}\"));\r\n			</c:when>\r\n			<c:when test=\"${p.dataType==4}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), DateUtil.formatDate(${entity.lowerClassName}.${p.getterName}()));\r\n			</c:when>\r\n			<c:when test=\"\">\r\n			\r\n			</c:when>\r\n		</c:choose>\r\n	</c:forEach>\r\n				result.add(rowMap);\r\n				index++;\r\n			} catch (Exception e) {\r\n	// log.info(\"${entity.name}表\",e);\r\n			}\r\n		}\r\n		return result;\r\n	}\r\n	// 根据Header名称获取map中的键名称\r\n	private String getPropNameByHeaderName(String header, SheetInfo sheetInfo) throws Exception {\r\n		int i = 0;\r\n		for (String h : sheetInfo.getHeaders()) {\r\n			if (h.equals(header)) {\r\n				break;\r\n			}\r\n			i++;\r\n		}\r\n		return \"prop_\" + i;\r\n	}\r\n</c:if>\r\n	<c:if test=\"${entity.importable==1}\">\r\n	\r\n	//导入相关---------------------------------------------------\r\n	@Override\r\n	public ImportResult importExcel(FileInputStream is) throws Exception{\r\n		${entity.className} ${entity.lowerClassName} = null;\r\n		ImportResult importResult = new ImportResult();\r\n        HSSFWorkbook hssfWorkbook = new HSSFWorkbook(is);\r\n        StringBuffer msg = new StringBuffer();\r\n        // 循环工作表Sheet\r\n        int total = 0;\r\n        int success = 0;\r\n        String cellContent;\r\n		Map<String,${entity.className}> data = new HashMap<String,${entity.className}>();\r\n        for (int numSheet = 0; numSheet < hssfWorkbook.getNumberOfSheets(); numSheet++) {\r\n            HSSFSheet hssfSheet = hssfWorkbook.getSheetAt(numSheet);\r\n            if (hssfSheet == null) {\r\n                continue;\r\n            }\r\n            // 循环行Row\r\n            for (int rowNum = 1; rowNum <= hssfSheet.getLastRowNum(); rowNum++) {\r\n            	total ++;\r\n                HSSFRow hssfRow = hssfSheet.getRow(rowNum);\r\n                if (hssfRow == null) {\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：空白数据</div>\");\r\n                	continue;\r\n                }\r\n                ${entity.lowerClassName} = new ${entity.className}();\r\n                try{\r\n                	assignmentPropValue(${entity.lowerClassName},hssfRow);\r\n                }catch (IllegalArgumentException e) {\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：\").append(e.getMessage()).append(\"</div>\");\r\n                	continue;\r\n				}\r\n                if(isRepeat(data,${entity.lowerClassName})){\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：重复数据</div>\");\r\n                	continue;\r\n                }\r\n                try{\r\n                	add(${entity.lowerClassName});\r\n                }catch (Exception e) {\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：数据解析完成，但是插入到数据库时失败:\").append(e.getMessage()).append(\"</div>\");\r\n                	e.printStackTrace();\r\n				}\r\n                success++;\r\n            }\r\n            //break;// 只读一个sheet页\r\n        }\r\n        importResult.setMessage(msg.toString());\r\n        importResult.setTotal(total);\r\n        importResult.setSuccess(success);\r\n		return importResult;\r\n	}\r\n	\r\n		/**\r\n		 * @Description 字段赋值\r\n		 * @param ${entity.lowerClassName} 待赋值的对象\r\n		 * @param hassfRow excel航对象\r\n		 */\r\n		private void assignmentPropValue(${entity.className} ${entity.lowerClassName}, HSSFRow hssfRow)throws IllegalArgumentException{\r\n\r\n		String cellContent;\r\n		<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">\r\n			<c:choose>\r\n				<c:when test=\"${p.dataType==1}\">\r\n					${entity.lowerClassName}.${p.setterName}( (String) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},null));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==2}\">\r\n					${entity.lowerClassName}.${p.setterName}( (Integer) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},null));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==3}\">\r\n					<c:choose>\r\n						<c:when test=\"${p.precision eq \'float\'}\">\r\n							${entity.lowerClassName}.${p.setterName}( (Float) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},\"${p.precision}\"));\r\n						</c:when>\r\n						<c:otherwise>\r\n							${entity.lowerClassName}.${p.setterName}( (Double) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},\"${p.precision}\"));\r\n						</c:otherwise>\r\n					</c:choose>\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==4}\">\r\n					${entity.lowerClassName}.${p.setterName}( (Date) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},null));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==6}\">\r\n					${entity.lowerClassName}.${p.setterName}( (${p.complexEntity.className}) cellContentToComplexObject( ${p.complexEntity.className}.class,${p.complexEntity.lowerClassName}Service,\"${p.complexEntity.idColumnName}\",getValue(hssfRow.getCell(${s.index}))));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==7}\">\r\n					cellContent = getValue(hssfRow.getCell(${s.index}));\r\n					for(Map.Entry<${p.complexEntity.className}, String> entry : ${p.complexEntity.className}.degreeMap.entrySet() ) {\r\n						if(entry.getValue().equals(cellContent)){\r\n							${entity.lowerClassName}.${p.setterName}(entry.getKey());\r\n						}\r\n					}\r\n				</c:when>\r\n			</c:choose>\r\n		</c:forEach>\r\n	}\r\n	\r\n\r\n	\r\n	/**\r\n	 * @Description 重复检查\r\n	 * @param ${entity.lowerClassName} 待检测对象\r\n	 * @param data 缓存数据\r\n	 * @return\r\n	 */\r\n	private boolean isRepeat(Map<String,${entity.className}> data,${entity.className} ${entity.lowerClassName}) {\r\n		boolean result = data.get(${entity.lowerClassName}.get...()) != null;\r\n		data.put(${entity.lowerClassName}.get...(), ${entity.lowerClassName});\r\n		return result;\r\n	}\r\n	\r\n	\r\n	</c:if>\r\n			\r\n			\r\n	@Override\r\n	public Page<HistoryRecord<${entity.className}>> getHistoryListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		pageInfo.setTotalCount(${entity.lowerClassName}Dao.findHistoryListRowCount(${entity.lowerClassName},pageInfo).intValue());\r\n		pageInfo.setOrderField(pageInfo.getOrderField() == null ? \"id\": pageInfo.getOrderField());\r\n		pageInfo.setOrderDirection(pageInfo.getOrderDirection() == null ? \"asc\": pageInfo.getOrderDirection());\r\n		pageInfo.setList(${entity.lowerClassName}Dao.findHistoryListPage(${entity.lowerClassName},pageInfo));\r\n        return pageInfo;\r\n	}\r\n	\r\n	@Override\r\n	public ${entity.className} findByVersion(Integer version) {\r\n		return ${entity.lowerClassName}Dao.findByVersion(version);\r\n	}\r\n}', '1', '2');
INSERT INTO `t_template` VALUES ('10', 'Dao接口代码', 'package ${entity.currentFileFullPackage};\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Criteria;\r\n\r\nimport com.novots.itsm.common.dao.IBaseDao;\r\nimport com.novots.itsm.util.web.Page;\r\nimport com.novots.itsm.history.entity.HistoryRecord;\r\n\r\nimport ${entity.fullClassName};\r\n/**\r\n * ${entity.name}接口\r\n * @author auto created by pc\r\n * <fmt:formatDate value=\"${NOW}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n */\r\npublic interface I${entity.className}Dao extends IBaseDao<${entity.className}> {\r\n\r\n    /**\r\n     * 查询记录数\r\n     * @param role\r\n     * @return\r\n     */\r\n	Long findListRowCount(${entity.className} ${entity.lowerClassName});\r\n/*\r\n*历史记录pagecount查询\r\n*/\r\n	Long findHistoryListRowCount(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n\r\n    /**\r\n     * 分页查询角色信息\r\n     * @param role\r\n     * @return\r\n     */\r\n    List<${entity.className}> findListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo);\r\n	/**\r\n	*历史记录分页list\r\n	*/\r\n    List<HistoryRecord<${entity.className}>> findHistoryListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo);\r\n	/**\r\n     *按照给定的版本号进行查询\r\n     */\r\n	${entity.className} findByVersion(Integer version);\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	/***\r\n	 * 查询所有记录列表\r\n	* @param ${entity.lowerClassName}\r\n	 * @param pageInfo\r\n	 * @return\r\n	 */\r\n	List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName}, Page pageInfo);\r\n</c:if>\r\n	/**\r\n	*添加查询条件\r\n	*/\r\n	public void queryCause(Criteria criteria, ${entity.className} ${entity.lowerClassName});\r\n}', '1', '2');
INSERT INTO `t_template` VALUES ('11', 'Dao实现代码', 'package ${entity.currentFileFullPackage};\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Criteria;\r\nimport org.hibernate.criterion.MatchMode;\r\nimport org.hibernate.criterion.Order;\r\nimport org.hibernate.criterion.Projections;\r\nimport org.hibernate.criterion.Restrictions;\r\nimport org.springframework.stereotype.Repository;\r\n\r\nimport org.hibernate.envers.AuditReader;\r\nimport org.hibernate.envers.AuditReaderFactory;\r\nimport org.hibernate.envers.RevisionType;\r\nimport org.hibernate.envers.query.AuditEntity;\r\nimport org.hibernate.envers.query.AuditQuery;\r\nimport org.hibernate.envers.query.AuditQueryCreator;\r\n\r\n\r\n\r\nimport com.novots.itsm.common.dao.IBaseDao;\r\nimport com.novots.itsm.util.web.Page;\r\nimport com.novots.itsm.history.entity.HistoryRecord;\r\n\r\nimport ${entity.fullClassName};\r\n/**\r\n * ${entity.name}Dao实现\r\n * @author auto created by pc\r\n * <fmt:formatDate value=\"${NOW}\" pattern=\"yyyy-MM-dd HH:mm:ss\"/>\r\n */\r\n@Repository\r\npublic class ${entity.className}DaoImpl extends BaseDaoImpl<${entity.className}> implements I${entity.className}Dao {\r\n\r\n<c:if test=\"${not empty entity.parent}\">\r\n	@Resource\r\n	private I${entity.parent.className}Dao ${entity.parent.lowerClassName}Dao;\r\n</c:if>\r\n\r\n    @Override\r\n    public Long findListRowCount(${entity.className} ${entity.lowerClassName}) {\r\n        Criteria criteria = getSession().createCriteria(${entity.className}.class).setProjection(Projections.rowCount());\r\n        queryCause(criteria,${entity.lowerClassName});\r\n        return (Long) criteria.uniqueResult();\r\n    }\r\n\r\n	@Override\r\n	public Long findHistoryListRowCount(${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n		AuditReader reader = AuditReaderFactory.get(getSession());\r\n		AuditQueryCreator queryCreator2 = reader.createQuery();\r\n		AuditQuery query = queryCreator2.forRevisionsOfEntity(${entity.className}.class, false, true);\r\n		query.add(AuditEntity.property(\"manageKey\").eq(${entity.lowerClassName}.getManageKey()));\r\n		queryCause(query,pageInfo);\r\n		query.addProjection(AuditEntity.property(\"manageKey\").count());\r\n		return (Long) query.getSingleResult();\r\n	}\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    @Override\r\n    public List<${entity.className}> findListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo){\r\n    	Criteria criteria = getSession().createCriteria(${entity.className}.class).setFirstResult(pageInfo.getStartIndex()).setMaxResults(pageInfo.getNumPerPage());\r\n        queryCause(criteria,${entity.lowerClassName});\r\n        if(pageInfo.getOrderField()!=null && !pageInfo.getOrderField().trim().equals(\"\")){\r\n         	if(pageInfo.getOrderDirection()==null || pageInfo.getOrderDirection().trim().equals(\"asc\")){\r\n         		criteria.addOrder(Order.asc(pageInfo.getOrderField()));\r\n         	}else{\r\n         		criteria.addOrder(Order.desc(pageInfo.getOrderField()));\r\n         	}\r\n         }else{\r\n         	criteria.addOrder(Order.asc(\"${entity.idColumnName}\")); \r\n         }\r\n        return criteria.list();\r\n    }\r\n\r\n	@SuppressWarnings(\"unchecked\")\r\n	@Override\r\n	public List<HistoryRecord<${entity.className}>> findHistoryListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		AuditReader reader = AuditReaderFactory.get(getSession());\r\n		AuditQueryCreator queryCreator2 = reader.createQuery();\r\n		AuditQuery query = queryCreator2.forRevisionsOfEntity(${entity.className}.class, false, true);\r\n		query.setFirstResult(pageInfo.getStartIndex()).setMaxResults(pageInfo.getNumPerPage());\r\n		query.add(AuditEntity.property(\"manageKey\").eq(${entity.lowerClassName}.getManageKey()));\r\n		queryCause(query,pageInfo);\r\n		if (pageInfo.getOrderField() != null && !pageInfo.getOrderField().trim().equals(\"\")) {\r\n			if (pageInfo.getOrderDirection() == null || pageInfo.getOrderDirection().trim().equals(\"asc\")) {\r\n				query.addOrder(AuditEntity.property(pageInfo.getOrderField()).asc());\r\n			} else {\r\n				query.addOrder(AuditEntity.property(pageInfo.getOrderField()).desc());\r\n			}\r\n		} else {\r\n			query.addOrder(AuditEntity.property(\"id\").desc());\r\n		}\r\n		List list = query.getResultList();\r\n		List<HistoryRecord<${entity.className}>> res = new ArrayList<HistoryRecord<${entity.className}>>();\r\n		for(Object obj : list){\r\n			Object[] array = (Object[])obj;\r\n			HistoryRecord<${entity.className}> record = new HistoryRecord<${entity.className}>();\r\n			record.setRecord((${entity.className})array[0]);\r\n			record.setRevisionEntity((TestRevisionEntity)array[1]);\r\n			record.setType((RevisionType)array[2]);\r\n			res.add(record);\r\n		}\r\n		return res;\r\n	}\r\n\r\n	public void queryCause(AuditQuery query,Page pageInfo) {\r\n		if(pageInfo.getOperateDateStart()!=null){\r\n			query.add(AuditEntity.revisionProperty(\"timestamp\").ge(pageInfo.getOperateDateStart()));\r\n		}\r\n		if(pageInfo.getOperateDateEnd()!=null){\r\n			query.add(AuditEntity.revisionProperty(\"timestamp\").le(pageInfo.getOperateDateEnd()));\r\n		}\r\n		if(pageInfo.getOperator()!=null && !pageInfo.getOperator().trim().equals(\"\")){\r\n			query.add(AuditEntity.revisionProperty(\"username\").like(pageInfo.getOperator(),MatchMode.ANYWHERE));\r\n		}\r\n		if(pageInfo.getOperateType()!=null && !pageInfo.getOperateType().trim().equals(\"\")){\r\n			query.add(AuditEntity.revisionType().eq(RevisionType.valueOf(pageInfo.getOperateType())));\r\n		}\r\n	}\r\n    \r\n    public void queryCause(Criteria criteria,${entity.className} ${entity.lowerClassName}){\r\n    	 #{ADD_CAUSE_ON_CRITERIA}\r\n\r\n<c:if test=\"${not empty entity.parent}\">\r\n	${entity.parent.lowerClassName}Dao.queryCause(criteria,${entity.lowerClassName});\r\n</c:if>\r\n    }\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	@Override\r\n	public List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		Criteria criteria = getSession().createCriteria(${entity.className}.class);\r\n		return executeCriteria(criteria, ${entity.lowerClassName}, pageInfo.getOrderField(),\r\n				pageInfo.getOrderDirection());\r\n	}\r\n	@SuppressWarnings(\"unchecked\")\r\n	private List<${entity.className}> executeCriteria(Criteria criteria,\r\n			${entity.className} ${entity.lowerClassName}, String orderField, String orderDirection) {\r\n		queryCause(criteria, ${entity.lowerClassName});\r\n		if (orderField != null && !orderField.trim().equals(\"\")) {\r\n			if (orderDirection == null || orderDirection.trim().equals(\"asc\")) {\r\n				criteria.addOrder(Order.asc(orderField));\r\n			} else {\r\n				criteria.addOrder(Order.desc(orderField));\r\n			}\r\n		} else {\r\n			criteria.addOrder(Order.asc(\"${entity.idColumnName}\"));\r\n		}\r\n		return criteria.list();\r\n	}\r\n</c:if>\r\n	\r\n	//按照给定的版本查询历史记录\r\n	@Override\r\n	public ${entity.className} findByVersion(Integer version) {\r\n		AuditReader reader = AuditReaderFactory.get(getSession());\r\n		AuditQueryCreator queryCreator2 = reader.createQuery();\r\n		AuditQuery query = queryCreator2.forEntitiesAtRevision(${entity.className}.class, version);\r\n		query.add(AuditEntity.revisionNumber().eq(version));\r\n		List list = query.getResultList();\r\n		if(list!=null && list.size()>0){\r\n			return (${entity.className})list.get(0);\r\n		}\r\n		return null;\r\n	}\r\n\r\n}', '1', '2');
