INSERT INTO `t_template` VALUES ('53', 'Controller层代码-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\n\r\nimport java.util.Date;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\nimport javax.annotation.Resource;\r\nimport javax.servlet.http.HttpServletRequest;\r\nimport javax.servlet.http.HttpSession;\r\nimport javax.validation.Valid;\r\n\r\nimport org.springframework.ui.Model;\r\nimport org.springframework.beans.factory.annotation.Autowired;\r\nimport org.springframework.stereotype.Controller;\r\nimport org.springframework.validation.BindingResult;\r\nimport org.springframework.validation.Errors;\r\nimport org.springframework.validation.FieldError;\r\nimport org.springframework.validation.Validator;\r\nimport org.springframework.web.bind.WebDataBinder;\r\nimport org.springframework.web.bind.annotation.InitBinder;\r\nimport org.springframework.web.bind.annotation.ModelAttribute;\r\nimport org.springframework.web.bind.annotation.PathVariable;\r\nimport org.springframework.web.bind.annotation.RequestMapping;\r\nimport org.springframework.web.bind.annotation.RequestMethod;\r\nimport org.springframework.web.bind.annotation.RequestParam;\r\nimport org.springframework.web.bind.annotation.ResponseBody;\r\n\r\nimport com.cniaas.catfish.asset.manage.functionci.entity.FunctionCI;\r\nimport com.cniaas.catfish.asset.manage.functionci.service.IFunctionCIService;\r\nimport com.cniaas.catfish.common.controller.BaseController;\r\nimport com.cniaas.catfish.common.entity.CommonFile;\r\nimport com.cniaas.catfish.common.entity.Select2;\r\nimport com.cniaas.catfish.common.service.ICommonFileService;\r\nimport com.cniaas.catfish.history.entity.HistoryRecord;\r\nimport com.cniaas.catfish.sys.entity.Department;\r\nimport com.cniaas.catfish.sys.entity.User;\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\nimport com.cniaas.catfish.sys.entity.propertyeditor.DepartmentEditor;\r\nimport com.cniaas.catfish.sys.entity.propertyeditor.UserEditor;\r\nimport com.cniaas.catfish.sys.service.IDepartmentService;\r\nimport com.cniaas.catfish.sys.service.IUserService;\r\nimport com.cniaas.catfish.ticket.configuration.entity.Category;\r\nimport com.cniaas.catfish.ticket.configuration.entity.Priority;\r\nimport com.cniaas.catfish.ticket.configuration.entity.propertyeditor.CategoryEditor;\r\nimport com.cniaas.catfish.ticket.configuration.entity.propertyeditor.PriorityEditor;\r\nimport com.cniaas.catfish.ticket.configuration.service.ICategoryService;\r\nimport com.cniaas.catfish.ticket.configuration.service.IPriorityService;\r\nimport com.cniaas.catfish.ticket.manage.entity.Ticket;\r\nimport com.cniaas.catfish.ticket.manage.entity.enumtype.OriginType;\r\nimport com.cniaas.catfish.ticket.manage.entity.enumtype.TicketStatus;\r\nimport com.cniaas.catfish.ticket.manage.service.ITicketService;\r\nimport com.cniaas.catfish.util.Md5Util;\r\nimport com.cniaas.catfish.util.bean.BeanPropUtil;\r\nimport com.cniaas.catfish.util.bean.CheckResult;\r\nimport com.cniaas.catfish.util.web.JsonResult;\r\nimport com.cniaas.catfish.util.web.Page;\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\n\r\n\r\nimport ${entity.fullClassName};\r\n\r\n<c:forEach items=\"${entity.children}\" var=\"child\">\r\nimport ${child.fullClassName};\r\n</c:forEach>\r\n\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.dataType==6}\">\r\nimport ${p.complexEntity.fullClassName};\r\n</c:if>\r\n</c:forEach>\r\n\r\nimport com.novots.itsm.util.Md5Util;\r\nimport com.novots.itsm.util.web.Page;\r\n\r\n<c:if test=\"${not empty entity.parent}\">\r\nimport ${entity.parent.fullClassName};\r\n</c:if>\r\n\r\n\r\n/**\r\n * ${entity.name}控制器\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\n\r\n@Controller\r\n@RequestMapping(\"${entity.lowerClassName}\")\r\npublic class ${entity.className}Controller  extends BaseController {\r\n\r\n	<c:if test=\"${not empty entity.parent}\">\r\n		/**${entity.parent.name}控制器 */ \r\n		@Resource\r\n		private ${entity.parent.className}Controller ${entity.parent.lowerClassName}Controller;\r\n	</c:if>\r\n\r\n   /**${entity.name}服务*/\r\n    @Resource\r\n    private I${entity.className}Service ${entity.lowerClassName}Service;\r\n /**\r\n  * 注册属性编辑器\r\n  * @param binder 数据绑定器\r\n  */\r\n\r\n    @InitBinder\r\n	public void initPropEditor(WebDataBinder binder){\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.initPropEditor(binder);\r\n</c:if>\r\n	    #{INIT_PROP_EDITOR}\r\n	}\r\n    \r\n    //复杂关联关系的Service\r\n    #{COMPLEX_SERVICES_DEFINE}\r\n    \r\n\r\n<c:choose>\r\n	<c:when test=\"${entity.manageKeySupport==1}\">\r\n		/**\r\n		  * 初始化ModelAttribute\r\n		  * @param manageKey md5管理ID （非空时自动加载指定对象）\r\n		  * @param model 页面model对象\r\n		  * @return ${entity.className} ${entity.name}对象\r\n		  */\r\n		@ModelAttribute(value=\"${entity.lowerClassName}\")\r\n		public ${entity.className} initModelAttribute(@RequestParam(value = \"manageKey\", required = false) String manageKey, Model model) {\r\n		${entity.className} ${entity.lowerClassName} = null;\r\n		if (manageKey == null ||manageKey.equals(\"\")) {\r\n			${entity.lowerClassName} = new ${entity.className}();\r\n		} else {\r\n			${entity.lowerClassName} =  ${entity.lowerClassName}Service.getEntityByProperty(${entity.className}.class,\"manageKey\", manageKey);\r\n			\r\n			if (${entity.lowerClassName}==null) {\r\n				${entity.lowerClassName}=new ${entity.className}();\r\n			}\r\n		}\r\n		return ${entity.lowerClassName};\r\n	}\r\n	</c:when>\r\n	<c:otherwise>\r\n		/**\r\n		  * 初始化ModelAttribute\r\n		  * @param ${entity.idColumnName} 唯一识别主键值\r\n		  * @return ${entity.className} ${entity.name}对象\r\n		  */\r\n	@ModelAttribute\r\n    public ${entity.className} initModelAttribute(@RequestParam(value=\"${entity.idColumnName}\",required=false) ${empty entity.idColumnProp ? \'Integer\':entity.idColumnProp.dataTypeName} ${entity.idColumnName}){\r\n    	${entity.className} ${entity.lowerClassName} = null;\r\n    	if(${entity.idColumnName}==null || ${entity.idColumnName}.equals(\"\")){\r\n    		${entity.lowerClassName} = new ${entity.className}();\r\n    	}else{\r\n    		${entity.lowerClassName} = ${entity.lowerClassName}Service.getById(${entity.className}.class, ${entity.idColumnName});\r\n    	}\r\n    	return ${entity.lowerClassName};\r\n    }\r\n	</c:otherwise>\r\n</c:choose>\r\n  \r\n    /**\r\n     * ${entity.name}分页查询\r\n     * @param request HttpServletRequest对象\r\n     * @param ${entity.lowerClassName} ${entity.name}查询对象\r\n     * @param pageInfo 分页查询对象\r\n     * \r\n     */\r\n    @RequestMapping(value=\"/list\")\r\n    public void list(HttpServletRequest request,${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n\r\n        Page<${entity.className}> ${entity.lowerClassName}Page = ${entity.lowerClassName}Service.getListPage(${entity.lowerClassName},pageInfo);\r\n        request.setAttribute(\"pageBean\", ${entity.lowerClassName}Page);\r\n        \r\n	     \r\n		listFormParam(request,${entity.lowerClassName},pageInfo);\r\n    }\r\n    \r\n 	/**\r\n     * 为${entity.name}分页查询表单准备数据\r\n     * @param request HttpServletRequest对象\r\n     * @param ${entity.lowerClassName} ${entity.name}查询对象\r\n     * @param pageInfo 分页查询对象\r\n     * \r\n     */\r\n	public void listFormParam(HttpServletRequest request,${entity.className} ${entity.lowerClassName},Page pageInfo){\r\n		//复杂关联关系数据准备\r\n    	#{COMPLEX_LIST_PREPARE_ADD_SMART_ADMIN}\r\n	\r\n		<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.listFormParam(request,${entity.lowerClassName},pageInfo);\r\n	    </c:if>\r\n	}\r\n	\r\n    /**\r\n     * 添加${entity.name}页面\r\n     * @param request HttpServletRequest对象\r\n     * @param callBack  回调函数名称\r\n     *\r\n     */\r\n\r\n    @RequestMapping(value=\"/add\",method=RequestMethod.GET)\r\n    public void addUI(HttpServletRequest request,@RequestParam(value = \"callBack\",required = false)String callBack) {\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.addUI(request,callBack);\r\n</c:if>\r\n    	//复杂关联关系数据准备\r\n        #{COMPLEX_LIST_PREPARE_ADD_SMART_ADMIN}\r\n		//clearTempDataList(request.getSession(),\"${entity.lowerClassName}<c:forEach items=\"${entity.parents}\" var=\"p\">,${p.lowerClassName}</c:forEach>\");\r\n		request.setAttribute(\"callBack\", callBack);\r\n    }\r\n    \r\n    /**\r\n     * 添加${entity.name}\r\n     * @param request HttpServletRequest对象\r\n     * @param ${entity.lowerClassName} 要添加的对象\r\n     * @return JsonResult 操作结果\r\n     */\r\n\r\n    @RequestMapping(value=\"/add\",method=RequestMethod.POST)\r\n    @ResponseBody\r\n    public JsonResult add(HttpServletRequest request,\r\n	@Valid @ModelAttribute(\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},\r\n	@RequestParam(value = \"callBack\",required = false)String callBack,\r\n	#{MANY_TO_MANY_IDS}\r\n	BindingResult result) {\r\n    	JsonResult jsonResult = new JsonResult();\r\n    	//默认系统时间类型保存\r\n    	#{DATATYPE_DEFAULT_VALUE_SAVE}\r\n	/*\r\n		#ONE_TO_MANY_VALUE_SAVE_ADD\r\n	*/\r\n	    //保存多对多关联关系\r\n		#{SAVE_MANY_TO_MANY_RELATION_ADD}\r\n\r\n	//保持一对多关联关系\r\n	#{SAVE_ONE_TO_MANY_RELATION_SMART_ADMIN}\r\n\r\n	<c:if test=\"${not empty entity.parent}\">\r\n		//调用父类添加方法，以保存多对多关系\r\n		#{INVOKE_PARENT_ADD_SMART_ADMIN}\r\n	</c:if>\r\n	\r\n	<c:if test=\"${entity.manageKeySupport==1 and empty entity.parent}\">\r\n	${entity.lowerClassName}.setManageKey(Md5Util.getManageKey());\r\n	</c:if>\r\n    	${entity.lowerClassName}Service.add(${entity.lowerClassName});\r\n        jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.add.success\"));\r\n\r\n        return jsonResult;\r\n    }\r\n    \r\n    /**\r\n     * 修改${entity.name}页面\r\n     * @param ${entity.lowerClassName} 需要修改的对象实体\r\n     * @param request HttpServletRequest请求对象\r\n     * \r\n     */\r\n    @RequestMapping(value=\"/update\",method=RequestMethod.GET)\r\n	public void updateUI(@ModelAttribute(value=\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},HttpServletRequest request) {\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.updateUI(${entity.lowerClassName},request);\r\n</c:if>\r\n	\r\n	request.setAttribute(\"tempBean\", ${entity.lowerClassName});\r\n    	//复杂关联关系数据准备\r\n        #{COMPLEX_LIST_PREPARE_UPDATE_SMART_ADMIN}\r\n	\r\n	//clearTempDataList(request.getSession(),\"${entity.lowerClassName}<c:forEach items=\"${entity.parents}\" var=\"p\">,${p.lowerClassName}</c:forEach>\");\r\n    }\r\n\r\n\r\n    \r\n    /**\r\n     *  修改${entity.name} \r\n     * @param ${entity.lowerClassName} 待修改的实体对象\r\n     * @param request HttpServletRequest对象\r\n     * @return JsonResult 操作结果\r\n     *\r\n     */\r\n    @RequestMapping(value=\"/update\",method=RequestMethod.POST)\r\n    @ResponseBody\r\n    public JsonResult update(HttpServletRequest request,\r\n	@ModelAttribute(value=\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},\r\n	#{MANY_TO_MANY_IDS}\r\n	BindingResult result) {\r\n    	JsonResult jsonResult = new JsonResult();\r\n    	if(!validate(jsonResult,result)){\r\n    		return jsonResult;\r\n    	}\r\n		\r\n	    //保存多对多关联关系\r\n		#{SAVE_MANY_TO_MANY_RELATION_UPDATE}\r\n\r\n		//保持一对多关联关系\r\n		#{SAVE_ONE_TO_MANY_RELATION_SMART_ADMIN}\r\n\r\n	\r\n	<c:if test=\"${not empty entity.parent}\">\r\n		//调用父类修改方法，以保存多对多关系\r\n		#{INVOKE_PARENT_UPDATE_SMART_ADMIN}\r\n	</c:if>\r\n        ${entity.lowerClassName}Service.update(${entity.lowerClassName});\r\n	// jsonResult.setNavTabId(rel);\r\n\r\n        jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.update.success\"));\r\n      \r\n        return jsonResult;\r\n    }\r\n	/**\r\n	 *  查看${entity.name}页面\r\n     * @param ${entity.lowerClassName} 需要查看的实体对象\r\n     * @param request HttpServletRequest对象\r\n     * \r\n     */\r\n    @RequestMapping(value=\"/detail\",method=RequestMethod.GET)\r\n	public void detail(@ModelAttribute(value=\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},HttpServletRequest request) {\r\n<c:if test=\"${not empty entity.parent}\">\r\n		${entity.parent.lowerClassName}Controller.detail(${entity.lowerClassName},request);\r\n</c:if>\r\n	\r\n	request.setAttribute(\"tempBean\", ${entity.lowerClassName});\r\n    	//复杂关联关系数据准备\r\n        listFormParam(request,${entity.lowerClassName},null);\r\n    }\r\n\r\n\r\n\r\n  \r\n	/**\r\n     * 删除单个${entity.name}对象\r\n     * @param request HttpServletRequest对象\r\n     * @param ${entity.lowerClassName} 待删除对象\r\n     * @return JsonResult 操作结果\r\n     * \r\n     */\r\n    @RequestMapping(value=\"/delete\",method=RequestMethod.GET)\r\n    @ResponseBody\r\n    public JsonResult delete(HttpServletRequest request,@ModelAttribute ${entity.className} ${entity.lowerClassName}) {\r\n        JsonResult jsonResult = new JsonResult();\r\n        jsonResult.setCallbackType(\"\");\r\n        try {\r\n        	${entity.lowerClassName}Service.delete(${entity.lowerClassName});\r\n		} catch (RuntimeException re) {\r\n			jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.inuse\"));\r\n			return jsonResult;\r\n		}catch (Exception e) {\r\n			jsonResult.setMessage(e.toString());\r\n			return jsonResult;\r\n		}\r\n	//jsonResult.setNavTabId(rel);\r\n        jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.success\"));\r\n        return jsonResult;\r\n    }\r\n\r\n\r\n		\r\n    /**\r\n     * 删除多个对象\r\n     * @param ${entity.manageId eq \'manageKey\' ? \'manageKeys\' : entity.manageId  }${entity.manageId eq \'manageKey\' ? \'\' : \'s\'  } 唯一识别ID数组\r\n     * @return JsonResult 操作结果\r\n     * \r\n     */\r\n\r\n    @RequestMapping(value=\"/delete\",method=RequestMethod.POST)\r\n    @ResponseBody\r\n	<c:choose>\r\n		<c:when  test=\"${entity.manageId eq \'manageKey\'}\">\r\n	\r\n	public JsonResult batchDelete(HttpServletRequest request,@RequestParam(\"manageKeys\") String[] manageKeys) {\r\n    	${entity.className} ${entity.lowerClassName} = null;\r\n		JsonResult jsonResult = new JsonResult();\r\n    	for(String manageKey : manageKeys){\r\n			 try {\r\n    			${entity.lowerClassName} = ${entity.lowerClassName}Service.getEntityByProperty(${entity.className}.class,\"manageKey\",manageKey);\r\n    			${entity.lowerClassName}Service.delete(${entity.lowerClassName});\r\n			}catch (RuntimeException re) {\r\n				jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.inuse\"));\r\n				return jsonResult;\r\n			}catch (Exception e) {\r\n				jsonResult.setMessage(e.toString());\r\n				return jsonResult;\r\n			}\r\n    	}\r\n		jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.success\"));\r\n    	return jsonResult;\r\n    } \r\n		\r\n		</c:when>\r\n		<c:otherwise>\r\n			\r\n	public JsonResult batchDelete(@RequestParam(\"${entity.manageId}s\") ${entity.idColumnProp.dataTypeName}[] ${entity.manageId}s) {\r\n    	${entity.className} ${entity.lowerClassName} = null;\r\n		for(${entity.idColumnProp.dataTypeName} id : ${entity.manageId}s){\r\n			 try {\r\n    			${entity.lowerClassName} = ${entity.lowerClassName}Service.loadById(${entity.className}.class, id);\r\n    			${entity.lowerClassName}Service.delete(${entity.lowerClassName});\r\n			}catch (RuntimeException re) {\r\n				jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.inuse\"));\r\n				return jsonResult;\r\n			}catch (Exception e) {\r\n				jsonResult.setMessage(e.toString());\r\n				return jsonResult;\r\n			}\r\n    	}\r\n    	JsonResult jsonResult = new JsonResult();\r\n		jsonResult.setMessage(getI18NMessage(request, \"base.common.controller.operate.delete.success\"));\r\n    	return jsonResult;\r\n    } \r\n		\r\n		</c:otherwise>\r\n	</c:choose>\r\n   \r\n	\r\n	\r\n	 /**\r\n	  *选择对象返回html\r\n      *@param request HttpServletRequest对象\r\n	  *@param manageKeys manageKey 数组\r\n	  *@return String 返回jsp页面路径\r\n      */\r\n	@RequestMapping(value = \"/selectList\",params = \"htmlType\")\r\n	public String selectListWithHtml(HttpServletRequest request,\r\n			@RequestParam(value = \"manageKey\",required = false)String[] manageKeys\r\n			){\r\n		List<${entity.className}> ${entity.lowerClassName}s = new ArrayList<${entity.className}>();\r\n		if(manageKeys!=null){\r\n			for(String manageKey : manageKeys){\r\n				${entity.className} temp_${entity.lowerClassName} = ${entity.lowerClassName}Service.getEntityByProperty(${entity.className}.class, \"manageKey\", manageKey);\r\n				if(temp_${entity.lowerClassName}!=null && !${entity.lowerClassName}s.contains(temp_${entity.lowerClassName})){\r\n					${entity.lowerClassName}s.add(temp_${entity.lowerClassName});\r\n				}\r\n			}\r\n		}\r\n		request.setAttribute(\"showCheckBox\",request.getParameter(\"showCheckBox\"));\r\n		request.setAttribute(\"keyName\",request.getParameter(\"keyName\"));\r\n		request.setAttribute(\"${entity.lowerClassName}s\",${entity.lowerClassName}s);\r\n		return \"${entity.lowerClassName}/select-list-html\";\r\n	}\r\n	\r\n	\r\n	/**\r\n	 *  历史记录列表\r\n     * @param request HttpServletRequest对象\r\n	 * @param ${entity.lowerClassName} 查询对象\r\n     * @param pageInfo 分页查询对象\r\n     * @return String 显示页面路径\r\n     * \r\n	 */\r\n	@RequestMapping(value = \"history\",params=\"list\")\r\n	public String historyList(HttpServletRequest request,${entity.className} ${entity.lowerClassName},Page pageInfo){\r\n		Page<HistoryRecord<${entity.className}>> ${entity.lowerClassName}Page = ${entity.lowerClassName}Service.getHistoryListPage(${entity.lowerClassName}, pageInfo);\r\n		request.setAttribute(\"pageBean\", ${entity.lowerClassName}Page);\r\n		request.setAttribute(\"manageKey\", ${entity.lowerClassName}.getManageKey());\r\n	    return \"${entity.lowerClassName}/history-list\";\r\n	} \r\n	\r\n	/**\r\n	 * 查看历史记录\r\n     * @param request HttpServletRequest对象\r\n     * @param version 版本号\r\n     * @return String 显示页面路径\r\n     *\r\n	 */\r\n	@RequestMapping(value = \"history\",params=\"view\")\r\n	public String historyView(HttpServletRequest request,@RequestParam(value = \"version\")Integer version){\r\n		${entity.className} ${entity.lowerClassName} = ${entity.lowerClassName}Service.findByVersion(version);\r\n		${entity.className} pre${entity.className} = (${entity.className})${entity.lowerClassName}Service.findByPreVersion(${entity.className}.class,${entity.lowerClassName}.getManageKey(),version);\r\n		if(pre${entity.className}!=null && pre${entity.className}.getManageKey().equals(${entity.lowerClassName}.getManageKey())){\r\n			Map<String,CheckResult> checkResult = BeanPropUtil.propValueCheck(pre${entity.className}, ${entity.lowerClassName});\r\n			request.setAttribute(\"checkResult\", checkResult);\r\n		}\r\n		request.setAttribute(\"tempBean\", ${entity.lowerClassName});\r\n		request.setAttribute(\"showOk\", \"no\");\r\n		request.setAttribute(\"historyView\", true);\r\n		return \"${entity.lowerClassName}/detail\";\r\n	}\r\n\r\n\r\n	/**\r\n	 * 根据关键字获取${entity.name}select2对象\r\n	 * @param queryKey 查询关键字\r\n	 * @param pageInfo 查询分页信息\r\n	 * @param response HttpServletResponse对象\r\n	 * @return Select2<${entity.className}> ${entity.name}Select2对象\r\n	 */\r\n	@RequestMapping(value=\"/select2Query\")\r\n	@ResponseBody \r\n	public Select2<${entity.className}> select2Query(String queryKey,Page<${entity.className}> pageInfo, HttpServletResponse response) {\r\n		Page<${entity.className}> page = ${entity.lowerClassName}Service.queryByKey(queryKey, pageInfo);\r\n		Select2<${entity.className}> select2 = new Select2<${entity.className}>();\r\n		select2.setTotal_count(page.getTotalCount());\r\n		select2.setItems(page.getList());\r\n		return select2;\r\n	}\r\n	\r\n	/**\r\n	 * 查找多个${entity.name}\r\n	 * @param ${entity.manageId eq \'username\' ? \'username\':\'id\'}s ${entity.manageId eq \'username\' ? \'username\':\'id\'}数组\r\n	 * @param response HttpServletResponse 对象\r\n	 * @return List<${entity.className}> ${entity.name}列表\r\n	 */\r\n	@RequestMapping(value=\"/queryById\")\r\n	@ResponseBody\r\npublic List<${entity.className}> queryById(${entity.manageId eq \'username\' ? \'String\':\'Integer\'}[] ${entity.manageId eq \'username\' ? \'username\':\'id\'}s,HttpServletResponse response) {\r\n		List<${entity.className}> ${entity.lowerClassName}List = ${entity.lowerClassName}Service.queryById(${entity.manageId eq \'username\' ? \'username\':\'id\'}s);\r\n		return ${entity.lowerClassName}List;\r\n	}\r\n\r\n	\r\n	/**\r\n	 * tab页添加表单\r\n	 * @param trTarget tr的target值\r\n	 * @param trIndex tr的序号\r\n     * @param propName 表单元素name前缀\r\n	 * @return String jsp路径\r\n	 */\r\n	@RequestMapping(value = \"/tabAddForm\")\r\npublic String tabAddForm(\r\n			@ModelAttribute(value=\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},\r\n			@RequestParam(value= \"trTarget\") String trTarget,\r\n			@RequestParam(value= \"trIndex\") Integer trIndex,\r\n			@RequestParam(value = \"propName\") String propName,\r\n			HttpServletRequest request) {\r\n		addUI(request, null);\r\n		request.setAttribute(\"tempBean\",${entity.lowerClassName});\r\n		request.setAttribute(\"trTarget\", trTarget);\r\n		request.setAttribute(\"trIndex\", trIndex);\r\n		request.setAttribute(\"propName\", propName);\r\n		return \"${entity.lowerClassName}/tab-add-form\";\r\n	}\r\n\r\n\r\n/**\r\n	 * tab页查看详情页面\r\n	 * @param trTarget tr的target值\r\n	 * @param trIndex tr的序号\r\n     * @param propName 表单元素name前缀\r\n	 * @return String jsp路径\r\n	 */\r\n	@RequestMapping(value = \"/tabDetailForm\")\r\n	public String tabDetailForm(\r\n			@ModelAttribute(value=\"${entity.lowerClassName}\") ${entity.className} ${entity.lowerClassName},\r\n			@RequestParam(value= \"trTarget\") String trTarget,\r\n			@RequestParam(value= \"trIndex\") Integer trIndex,\r\n			@RequestParam(value = \"propName\") String propName,\r\n			HttpServletRequest request) {\r\n		addUI(request, null);\r\n		request.setAttribute(\"tempBean\",${entity.lowerClassName});\r\n		request.setAttribute(\"trTarget\", trTarget);\r\n		request.setAttribute(\"trIndex\", trIndex);\r\n		request.setAttribute(\"propName\", propName);\r\n		return \"${entity.lowerClassName}/tab-detail-form\";\r\n	}\r\n\r\n    \r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('54', 'Service接口代码-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\n\r\n\r\n\r\nimport java.text.SimpleDateFormat;\r\nimport java.util.ArrayList;\r\nimport java.util.Date;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\n\r\nimport com.cniaas.catfish.common.service.IBaseService;\r\nimport com.cniaas.catfish.history.entity.HistoryRecord;\r\nimport com.cniaas.catfish.ticket.manage.entity.Ticket;\r\nimport com.cniaas.catfish.util.web.Page;\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\n\r\nimport ${entity.fullClassName};\r\n/**\r\n * ${entity.name}Service接口\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\npublic interface I${entity.className}Service extends IBaseService<${entity.className}> {\r\n\r\n    /**\r\n     * ${entity.name}分页查询\r\n     * @param ${entity.lowerClassName} 查询对象\r\n     * @param pageInfo 分页查询对象\r\n     * @return Page<${entity.className}> ${entity.name}分页信息对象\r\n     */\r\n    public Page<${entity.className}> getListPage(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n   /**\r\n	*查询所有${entity.name}记录\r\n	*@param ${entity.lowerClassName} 查询对象\r\n	*@param pageInfo 分页查询对象\r\n	*@return List<${entity.className}>  ${entity.name}列表\r\n	*/\r\n	public List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n   \r\n    /**\r\n	 * 导出${entity.name}信息\r\n     * @param response HttpServletResponse对象\r\n	 * @param dataList 需要导出的${entity.name}列表\r\n     * \r\n     */\r\n    public void export(HttpServletResponse response,List<${entity.className}> dataList);\r\n</c:if>\r\n	<c:if test=\"${entity.importable==1}\">\r\n	\r\n    /**\r\n	 * 导入${entity.name}记录\r\n	 * @param is 文件输入流\r\n	 * @return ImportResult 导入结果\r\n	 */\r\n	public ImportResult importExcel(FileInputStream is) throws Exception;\r\n	\r\n	\r\n	</c:if>\r\n	\r\n	/**\r\n	  * ${entity.name}历史记录分页查询\r\n	  * @param ${entity.lowerClassName} 查询对象\r\n	  * @param pageInfo 分页查询对象\r\n	  * @return Page<HistoryRecord<${entity.className}>> ${entity.name}历史分页信息对象\r\n	  */\r\n	public Page<HistoryRecord<${entity.className}>> getHistoryListPage(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n\r\n	/**\r\n	 * ${entity.name}版本号查询\r\n	 * @param version 查询版本号\r\n	 * @return ${entity.className} ${entity.name}历史记录\r\n	 */\r\n	public ${entity.className} findByVersion(Integer version);\r\n	\r\n	\r\n	/**\r\n	 * ${entity.name}关键字查询\r\n	 * @param  queryKey 关键字\r\n	 * @param  pageInfo 分页查询对象\r\n	 * @return Page<${entity.className}> ${entity.name}分页信息对象\r\n	 * \r\n	 */\r\n	Page<${entity.className}> queryByKey(String queryKey, Page<${entity.className}> pageInfo);\r\n\r\n	/**\r\n	 * 查找多个${entity.name}\r\n	 * @param ${entity.manageId eq \'username\' ? \'username\':\'id\'}s ${entity.manageId eq \'username\' ? \'username\':\'id\'}数组\r\n	 * @return List<${entity.className}> ${entity.name}列表\r\n	 * \r\n	 */\r\n	List<${entity.className}> queryById(${entity.manageId eq \'username\' ? \'String\':\'Integer\'}[] ${entity.manageId eq \'username\' ? \'username\':\'id\'}s);\r\n	\r\n	\r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('55', 'Service实现代码-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\n\r\nimport java.util.List;\r\n\r\nimport javax.annotation.Resource;\r\nimport org.springframework.stereotype.Service;\r\n\r\n\r\nimport org.apache.poi.hssf.usermodel.HSSFCell;\r\nimport org.apache.poi.hssf.usermodel.HSSFRow;\r\nimport org.apache.poi.hssf.usermodel.HSSFSheet;\r\nimport org.apache.poi.hssf.usermodel.HSSFWorkbook;\r\n\r\nimport com.cniaas.catfish.common.service.impl.BaseServiceImpl;\r\nimport com.cniaas.catfish.history.entity.HistoryRecord;\r\nimport com.cniaas.catfish.ticket.manage.dao.ITicketDao;\r\nimport com.cniaas.catfish.ticket.manage.entity.Ticket;\r\nimport com.cniaas.catfish.ticket.manage.service.ITicketService;\r\nimport com.cniaas.catfish.util.web.Page;\r\n\r\nimport ${entity.fullClassName};\r\nimport com.novots.itsm.history.entity.HistoryRecord;\r\n\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\n\r\n/**\r\n * ${entity.name}Service接口实现\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\n@Service\r\npublic class ${entity.className}ServiceImpl extends BaseServiceImpl<${entity.className}> implements I${entity.className}Service {\r\n\r\n    /**${entity.name}DAO对象*/\r\n    @Resource\r\n    private I${entity.className}Dao ${entity.lowerClassName}Dao;\r\n    \r\n    @Override\r\n    public Page<${entity.className}> getListPage(${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n      	pageInfo.setTotalCount(${entity.lowerClassName}Dao.findListRowCount(${entity.lowerClassName}).intValue());\r\n		pageInfo.setOrderField(pageInfo.getOrderField() == null ? \"id\": pageInfo.getOrderField());\r\n		pageInfo.setOrderDirection(pageInfo.getOrderDirection() == null ? \"asc\": pageInfo.getOrderDirection());\r\n		pageInfo.setList(${entity.lowerClassName}Dao.findListPage(${entity.lowerClassName},pageInfo));\r\n        return pageInfo;\r\n    }\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	@Override\r\n	public List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n		return ${entity.lowerClassName}Dao.findAllList(${entity.lowerClassName},pageInfo);\r\n	}\r\n	@Override\r\n	public void export(HttpServletResponse response,List<${entity.className}> dataList) {\r\n	// 格式化后的${entity.name}LIST\r\n		SheetInfo sheetInfo = get${entity.className}SheetInfo();\r\n		sheetInfo.setData(complexObjectDateToMapList(dataList, sheetInfo));\r\n\r\n		List<SheetInfo> export${entity.className}List = new ArrayList<SheetInfo>();\r\n		export${entity.className}List.add(sheetInfo);\r\n		ExportPOIUtil.exportExcel(response, export${entity.className}List, sheetInfo.getSheetName());\r\n	}\r\n	// 设置格式\r\n	private SheetInfo get${entity.className}SheetInfo() {\r\n	StringBuffer title= new StringBuffer(\"${entity.name}信息\");\r\n		title.append(new SimpleDateFormat(\"yyyyMMdd\").format(new Date()));\r\n	\r\n		SheetInfo sheetInfo = new SheetInfo(title.toString());\r\n	sheetInfo.setHeaders(new String[] {<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">\"${p.name}\"${not s.last ? \',\':\'\'}</c:forEach>});\r\n		sheetInfo.setPropertys(createPropsWithHeaders(sheetInfo.getHeaders()));\r\n	sheetInfo.setColwidth(new int[] {<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">15${not s.last ? \',\':\'\'}</c:forEach>});\r\n		sheetInfo.setCellTypes(new int[] { \r\n	<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">HSSFCell.CELL_TYPE_STRING, HSSFCell.CELL_TYPE_STRING${not s.last ? \',\':\'\'}</c:forEach>});\r\n		sheetInfo.setDefaultCell(sheetInfo.getHeaders().length, HSSFCell.CELL_TYPE_STRING);\r\n		sheetInfo.setDefaultCell(sheetInfo.getHeaders().length, HSSFCell.CELL_TYPE_STRING);\r\n		sheetInfo.setDefaultHeadSytle(sheetInfo.getHeaders().length,SheetInfo.CELL_STYLE_ORANGE);\r\n		sheetInfo.setDefaultOper(sheetInfo.getHeaders().length, SheetInfo.OPER_NO);\r\n		return sheetInfo;\r\n	}\r\n	/**\r\n	*根据headers创建properties\r\n	*@param headers 列表头数组\r\n	*@return String[] properties数组\r\n	*/\r\n	private String[] createPropsWithHeaders(String[] headers) {\r\n		List<String> props = new ArrayList<String>();\r\n		int i = 0;\r\n		for (String header : headers) {\r\n			props.add(\"prop_\" + (i++));\r\n		}\r\n		String[] res = new String[headers.length];\r\n		i = 0;\r\n		for (Object obj : props.toArray()) {\r\n			res[i++] = (String) obj;\r\n		}\r\n		return res;\r\n	}\r\n	/**\r\n	*${entity.name}信息数据整理\r\n	*@param data ${entity.name}列表\r\n	*@param sheetInfo excel页\r\n	*@return List<Map<String, String>> ${entity.name}整理结果\r\n	*/\r\n	private List<Map<String, String>> complexObjectDateToMapList(List<${entity.className}> data, SheetInfo sheetInfo) {\r\n		List<Map<String, String>> result = new ArrayList<Map<String, String>>();\r\n		Map<String, String> rowMap = null;\r\n		int index = 1;\r\n	for (${entity.className} ${entity.lowerClassName} : data) {\r\n			try {\r\n				rowMap = new HashMap<String, String>();\r\n				\r\n				rowMap.put(getPropNameByHeaderName(\"序号\", sheetInfo), index + \"\");\r\n	<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">\r\n		<c:choose>\r\n			<c:when test=\"${p.dataType==1 or p.dataType==2 or p.dataType==3}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), StringUtil.mildToString(${entity.lowerClassName},\"${p.upperCasePropName}\"));\r\n			</c:when>\r\n			<c:when test=\"${p.dataType==7}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), StringUtil.mildToString(${entity.lowerClassName},\"${p.upperCasePropName}\",\"Description\"));\r\n			</c:when>\r\n			<c:when test=\"${p.dataType==6}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), StringUtil.mildToString(${entity.lowerClassName},\"${p.upperCasePropName}\",\"${p.upperCaseValuePath}\"));\r\n			</c:when>\r\n			<c:when test=\"${p.dataType==4}\">\r\n				rowMap.put(getPropNameByHeaderName(\"${p.name}\", sheetInfo), DateUtil.formatDate(${entity.lowerClassName}.${p.getterName}()));\r\n			</c:when>\r\n			<c:when test=\"\">\r\n			\r\n			</c:when>\r\n		</c:choose>\r\n	</c:forEach>\r\n				result.add(rowMap);\r\n				index++;\r\n			} catch (Exception e) {\r\n	// log.info(\"${entity.name}表\",e);\r\n			}\r\n		}\r\n		return result;\r\n	}\r\n	/**\r\n	*根据Header名称获取map中的键名称\r\n	*@param header 表头\r\n	*@param sheetInfo excel页\r\n	*@return String 键名称\r\n	*/\r\n	private String getPropNameByHeaderName(String header, SheetInfo sheetInfo) throws Exception {\r\n		int i = 0;\r\n		for (String h : sheetInfo.getHeaders()) {\r\n			if (h.equals(header)) {\r\n				break;\r\n			}\r\n			i++;\r\n		}\r\n		return \"prop_\" + i;\r\n	}\r\n</c:if>\r\n	<c:if test=\"${entity.importable==1}\">\r\n	\r\n	//导入相关---------------------------------------------------\r\n	@Override\r\n	public ImportResult importExcel(FileInputStream is) throws Exception{\r\n		${entity.className} ${entity.lowerClassName} = null;\r\n		ImportResult importResult = new ImportResult();\r\n        HSSFWorkbook hssfWorkbook = new HSSFWorkbook(is);\r\n        StringBuffer msg = new StringBuffer();\r\n        // 循环工作表Sheet\r\n        int total = 0;\r\n        int success = 0;\r\n        String cellContent;\r\n		Map<String,${entity.className}> data = new HashMap<String,${entity.className}>();\r\n        for (int numSheet = 0; numSheet < hssfWorkbook.getNumberOfSheets(); numSheet++) {\r\n            HSSFSheet hssfSheet = hssfWorkbook.getSheetAt(numSheet);\r\n            if (hssfSheet == null) {\r\n                continue;\r\n            }\r\n            // 循环行Row\r\n            for (int rowNum = 1; rowNum <= hssfSheet.getLastRowNum(); rowNum++) {\r\n            	total ++;\r\n                HSSFRow hssfRow = hssfSheet.getRow(rowNum);\r\n                if (hssfRow == null) {\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：空白数据</div>\");\r\n                	continue;\r\n                }\r\n                ${entity.lowerClassName} = new ${entity.className}();\r\n                try{\r\n                	assignmentPropValue(${entity.lowerClassName},hssfRow);\r\n                }catch (IllegalArgumentException e) {\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：\").append(e.getMessage()).append(\"</div>\");\r\n                	continue;\r\n				}\r\n                if(isRepeat(data,${entity.lowerClassName})){\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：重复数据</div>\");\r\n                	continue;\r\n                }\r\n                try{\r\n                	add(${entity.lowerClassName});\r\n                }catch (Exception e) {\r\n                	msg.append(\"<div class=\'repeat\'>第<span class=\'rowNum\'>\").append(rowNum+1).append(\"</span>行：数据解析完成，但是插入到数据库时失败:\").append(e.getMessage()).append(\"</div>\");\r\n                	e.printStackTrace();\r\n				}\r\n                success++;\r\n            }\r\n            //break;// 只读一个sheet页\r\n        }\r\n        importResult.setMessage(msg.toString());\r\n        importResult.setTotal(total);\r\n        importResult.setSuccess(success);\r\n		return importResult;\r\n	}\r\n	\r\n	/**\r\n	 * 字段赋值\r\n	 * @param ${entity.lowerClassName} 需要赋值的对象\r\n	 * @param hssfRow excel行对象\r\n	 */\r\n		private void assignmentPropValue(${entity.className} ${entity.lowerClassName}, HSSFRow hssfRow)throws IllegalArgumentException{\r\n\r\n		String cellContent;\r\n		<c:forEach items=\"${entity.properties}\" var=\"p\" varStatus=\"s\">\r\n			<c:choose>\r\n				<c:when test=\"${p.dataType==1}\">\r\n					${entity.lowerClassName}.${p.setterName}( (String) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},null));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==2}\">\r\n					${entity.lowerClassName}.${p.setterName}( (Integer) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},null));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==3}\">\r\n					<c:choose>\r\n						<c:when test=\"${p.precision eq \'float\'}\">\r\n							${entity.lowerClassName}.${p.setterName}( (Float) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},\"${p.precision}\"));\r\n						</c:when>\r\n						<c:otherwise>\r\n							${entity.lowerClassName}.${p.setterName}( (Double) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},\"${p.precision}\"));\r\n						</c:otherwise>\r\n					</c:choose>\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==4}\">\r\n					${entity.lowerClassName}.${p.setterName}( (Date) cellContentToSampleObject( getValue(hssfRow.getCell(${s.index})),${p.dataType},null));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==6}\">\r\n					${entity.lowerClassName}.${p.setterName}( (${p.complexEntity.className}) cellContentToComplexObject( ${p.complexEntity.className}.class,${p.complexEntity.lowerClassName}Service,\"${p.complexEntity.idColumnName}\",getValue(hssfRow.getCell(${s.index}))));\r\n				</c:when>\r\n				<c:when test=\"${p.dataType==7}\">\r\n					cellContent = getValue(hssfRow.getCell(${s.index}));\r\n					for(Map.Entry<${p.complexEntity.className}, String> entry : ${p.complexEntity.className}.degreeMap.entrySet() ) {\r\n						if(entry.getValue().equals(cellContent)){\r\n							${entity.lowerClassName}.${p.setterName}(entry.getKey());\r\n						}\r\n					}\r\n				</c:when>\r\n			</c:choose>\r\n		</c:forEach>\r\n	}\r\n	\r\n\r\n	\r\n	/**\r\n	 * 重复检查\r\n	 * @param data 缓存数据\r\n	 * @param ${entity.lowerClassName} 待检测的对象\r\n	 * @return  boolean  是否重复结果\r\n	 */\r\n	private boolean isRepeat(Map<String,${entity.className}> data,${entity.className} ${entity.lowerClassName}) {\r\n		boolean result = data.get(${entity.lowerClassName}.get...()) != null;\r\n		data.put(${entity.lowerClassName}.get...(), ${entity.lowerClassName});\r\n		return result;\r\n	}\r\n	\r\n	\r\n	</c:if>\r\n			\r\n			\r\n	@Override\r\n	public Page<HistoryRecord<${entity.className}>> getHistoryListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		pageInfo.setTotalCount(${entity.lowerClassName}Dao.findHistoryListRowCount(${entity.lowerClassName},pageInfo).intValue());\r\n		pageInfo.setOrderField(pageInfo.getOrderField() == null ? \"id\": pageInfo.getOrderField());\r\n		pageInfo.setOrderDirection(pageInfo.getOrderDirection() == null ? \"asc\": pageInfo.getOrderDirection());\r\n		pageInfo.setList(${entity.lowerClassName}Dao.findHistoryListPage(${entity.lowerClassName},pageInfo));\r\n        return pageInfo;\r\n	}\r\n	\r\n	@Override\r\n	public ${entity.className} findByVersion(Integer version) {\r\n		return ${entity.lowerClassName}Dao.findByVersion(version);\r\n	}\r\n			\r\n	@Override\r\n	public Page<${entity.className}> queryByKey(String queryKey, Page<${entity.className}> pageInfo) {\r\n		int countRecord = ${entity.lowerClassName}Dao.findRowCount(queryKey).intValue();\r\n		Page<${entity.className}> page = new Page<${entity.className}>(pageInfo == null ? 0 : pageInfo.getPageNum(), countRecord, pageInfo.getNumPerPage());\r\n		List<${entity.className}> ${entity.lowerClassName}List = ${entity.lowerClassName}Dao.findByKey(pageInfo,queryKey);\r\n		page.setList(${entity.lowerClassName}List);\r\n		return page;\r\n	}\r\n\r\n	@Override\r\n	public List<${entity.className}> queryById(${entity.manageId eq \'username\' ? \'String\':\'Integer\'}[] ${entity.manageId eq \'username\' ? \'username\':\'id\'}s) {\r\n		return ${entity.lowerClassName}Dao.findById(${entity.manageId eq \'username\' ? \'username\':\'id\'}s);\r\n	}\r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('56', 'Dao接口代码-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Criteria;\r\n\r\nimport com.cniaas.catfish.common.dao.IBaseDao;\r\nimport com.cniaas.catfish.history.entity.HistoryRecord;\r\nimport com.cniaas.catfish.ticket.manage.entity.Ticket;\r\nimport com.cniaas.catfish.util.web.Page;\r\n\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\n\r\nimport ${entity.fullClassName};\r\n\r\n/**\r\n * ${entity.name}DAO接口\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\npublic interface I${entity.className}Dao extends IBaseDao<${entity.className}> {\r\n\r\n    /**\r\n     * ${entity.name}查询记录数量\r\n     * @param ${entity.lowerClassName} 查询对象\r\n     * @return Long 记录数量\r\n     */\r\n	Long findListRowCount(${entity.className} ${entity.lowerClassName});\r\n	/**\r\n	 * ${entity.name}历史记录数量\r\n     * @param ${entity.lowerClassName} 查询对象\r\n     * @param pageInfo 分页查询对象\r\n	 * @return Long 历史记录数量\r\n     */\r\n	Long findHistoryListRowCount(${entity.className} ${entity.lowerClassName},Page pageInfo);\r\n\r\n    /**\r\n     * ${entity.name}分页查询\r\n     * @param ${entity.lowerClassName} 查询对象\r\n     * @param pageInfo 分页查询对象\r\n     * @return  List<${entity.className}>  ${entity.name}列表 \r\n     */\r\n    List<${entity.className}> findListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo);\r\n	/**\r\n	 * ${entity.name}历史记录分页查询\r\n     * @param ${entity.lowerClassName} 查询对象\r\n     * @param pageInfo 分页查询对象\r\n     * @return List<HistoryRecord<${entity.className}>>  ${entity.name}历史列表\r\n	 */\r\n    List<HistoryRecord<${entity.className}>> findHistoryListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo);\r\n	/**\r\n     * ${entity.name}版本号查询\r\n     * @param version 版本号\r\n	 * @return ${entity.className} ${entity.name}历史记录\r\n     */\r\n	${entity.className} findByVersion(Integer version);\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	/***\r\n	 * 查询所有${entity.name}记录\r\n	 * @param ${entity.lowerClassName} 查询对象\r\n	 * @param pageInfo 分页查询对象\r\n	 * @return List<${entity.className}>  ${entity.name}列表\r\n	 */\r\n	List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName}, Page pageInfo);\r\n</c:if>\r\n	/**\r\n	 * 添加${entity.name}查询条件\r\n	 * @param criteria Hibernate Criteria对象\r\n	 * @param ${entity.lowerClassName} 查询对象\r\n	 */\r\n	public void queryCause(Criteria criteria, ${entity.className} ${entity.lowerClassName});\r\n	\r\n	/**\r\n	 * ${entity.name}关键字查询\r\n	 * @param  queryKey 查询关键字\r\n	 * @return List<${entity.className}> ${entity.name}列表\r\n	 */\r\n	List<${entity.className}> findByKey(Page pageInfo,String queryKey);\r\n\r\n	/**\r\n	 * ${entity.name}关键字查询数量\r\n	 * @param queryKey 查询关键字\r\n	 * @return Long 记录数量\r\n	 */\r\n	Long findRowCount(String queryKey);\r\n	\r\n    /**\r\n     * 查找多个${entity.name}\r\n     * @param  ${entity.manageId eq \'username\' ? \'username\':\'id\'}s ${entity.manageId eq \'username\' ? \'username\':\'id\'}数组\r\n	 * @return List<${entity.className}>  ${entity.name}列表\r\n     */\r\n	List<${entity.className}> findById(${entity.manageId eq \'username\' ? \'String\':\'Integer\'}[] ${entity.manageId eq \'username\' ? \'username\':\'id\'}s);\r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('57', 'Dao实现代码-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Criteria;\r\nimport org.hibernate.criterion.MatchMode;\r\nimport org.hibernate.criterion.Order;\r\nimport org.hibernate.criterion.Projections;\r\nimport org.hibernate.criterion.Restrictions;\r\nimport org.hibernate.envers.AuditReader;\r\nimport org.hibernate.envers.AuditReaderFactory;\r\nimport org.hibernate.envers.RevisionType;\r\nimport org.hibernate.envers.query.AuditEntity;\r\nimport org.hibernate.envers.query.AuditQuery;\r\nimport org.hibernate.envers.query.AuditQueryCreator;\r\nimport org.springframework.stereotype.Repository;\r\n\r\nimport com.cniaas.catfish.common.dao.impl.BaseDaoImpl;\r\nimport com.cniaas.catfish.history.entity.HistoryRecord;\r\nimport com.cniaas.catfish.history.entity.TestRevisionEntity;\r\nimport com.cniaas.catfish.ticket.manage.dao.ITicketDao;\r\nimport com.cniaas.catfish.ticket.manage.entity.Ticket;\r\nimport com.cniaas.catfish.util.web.Page;\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\n\r\nimport ${entity.fullClassName};\r\n\r\n/**\r\n * ${entity.name}DAO实现\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\n@Repository\r\npublic class ${entity.className}DaoImpl extends BaseDaoImpl<${entity.className}> implements I${entity.className}Dao {\r\n\r\n<c:if test=\"${not empty entity.parent}\">\r\n	@Resource\r\n	private I${entity.parent.className}Dao ${entity.parent.lowerClassName}Dao;\r\n</c:if>\r\n\r\n    @Override\r\n    public Long findListRowCount(${entity.className} ${entity.lowerClassName}) {\r\n        Criteria criteria = getSession().createCriteria(${entity.className}.class).setProjection(Projections.rowCount());\r\n        queryCause(criteria,${entity.lowerClassName});\r\n        return (Long) criteria.uniqueResult();\r\n    }\r\n\r\n	@Override\r\n	public Long findHistoryListRowCount(${entity.className} ${entity.lowerClassName},Page pageInfo) {\r\n		AuditReader reader = AuditReaderFactory.get(getSession());\r\n		AuditQueryCreator queryCreator2 = reader.createQuery();\r\n		AuditQuery query = queryCreator2.forRevisionsOfEntity(${entity.className}.class, false, true);\r\n		query.add(AuditEntity.property(\"manageKey\").eq(${entity.lowerClassName}.getManageKey()));\r\n		queryCause(query,pageInfo);\r\n		query.addProjection(AuditEntity.property(\"manageKey\").count());\r\n		return (Long) query.getSingleResult();\r\n	}\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    @Override\r\n    public List<${entity.className}> findListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo){\r\n    	Criteria criteria = getSession().createCriteria(${entity.className}.class).setFirstResult(pageInfo.getStartIndex()).setMaxResults(pageInfo.getNumPerPage());\r\n        queryCause(criteria,${entity.lowerClassName});\r\n        if(pageInfo.getOrderField()!=null && !pageInfo.getOrderField().trim().equals(\"\")){\r\n         	if(pageInfo.getOrderDirection()==null || pageInfo.getOrderDirection().trim().equals(\"asc\")){\r\n         		criteria.addOrder(Order.asc(pageInfo.getOrderField()));\r\n         	}else{\r\n         		criteria.addOrder(Order.desc(pageInfo.getOrderField()));\r\n         	}\r\n         }else{\r\n         	criteria.addOrder(Order.asc(\"${entity.idColumnName}\")); \r\n         }\r\n        return criteria.list();\r\n    }\r\n\r\n	@SuppressWarnings(\"unchecked\")\r\n	@Override\r\n	public List<HistoryRecord<${entity.className}>> findHistoryListPage(${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		AuditReader reader = AuditReaderFactory.get(getSession());\r\n		AuditQueryCreator queryCreator2 = reader.createQuery();\r\n		AuditQuery query = queryCreator2.forRevisionsOfEntity(${entity.className}.class, false, true);\r\n		query.setFirstResult(pageInfo.getStartIndex()).setMaxResults(pageInfo.getNumPerPage());\r\n		query.add(AuditEntity.property(\"manageKey\").eq(${entity.lowerClassName}.getManageKey()));\r\n		queryCause(query,pageInfo);\r\n		if (pageInfo.getOrderField() != null && !pageInfo.getOrderField().trim().equals(\"\")) {\r\n			if (pageInfo.getOrderDirection() == null || pageInfo.getOrderDirection().trim().equals(\"asc\")) {\r\n				query.addOrder(AuditEntity.property(pageInfo.getOrderField()).asc());\r\n			} else {\r\n				query.addOrder(AuditEntity.property(pageInfo.getOrderField()).desc());\r\n			}\r\n		} else {\r\n			query.addOrder(AuditEntity.property(\"id\").desc());\r\n		}\r\n		List list = query.getResultList();\r\n		List<HistoryRecord<${entity.className}>> res = new ArrayList<HistoryRecord<${entity.className}>>();\r\n		for(Object obj : list){\r\n			Object[] array = (Object[])obj;\r\n			HistoryRecord<${entity.className}> record = new HistoryRecord<${entity.className}>();\r\n			record.setRecord((${entity.className})array[0]);\r\n			record.setRevisionEntity((TestRevisionEntity)array[1]);\r\n			record.setType((RevisionType)array[2]);\r\n			res.add(record);\r\n		}\r\n		return res;\r\n	}\r\n\r\n	/**\r\n	  * 添加历史记录查询条件\r\n      * @param query 历史查询对象\r\n      * @param pageInfo 历史记录分页查询对象\r\n	  */\r\n	public void queryCause(AuditQuery query,Page pageInfo) {\r\n		if(pageInfo.getOperateDateStart()!=null){\r\n			query.add(AuditEntity.revisionProperty(\"timestamp\").ge(pageInfo.getOperateDateStart()));\r\n		}\r\n		if(pageInfo.getOperateDateEnd()!=null){\r\n			query.add(AuditEntity.revisionProperty(\"timestamp\").le(pageInfo.getOperateDateEnd()));\r\n		}\r\n		if(pageInfo.getOperator()!=null && !pageInfo.getOperator().trim().equals(\"\")){\r\n			query.add(AuditEntity.revisionProperty(\"username\").like(pageInfo.getOperator(),MatchMode.ANYWHERE));\r\n		}\r\n		if(pageInfo.getOperateType()!=null && !pageInfo.getOperateType().trim().equals(\"\")){\r\n			query.add(AuditEntity.revisionType().eq(RevisionType.valueOf(pageInfo.getOperateType())));\r\n		}\r\n	}\r\n    \r\n	/**\r\n	  * 根据查询对象往criteria对象增加查询条件\r\n      * @param criteria Hibernate criteria对象\r\n      * @param ${entity.lowerClassName} 查询对象\r\n	  */\r\n    public void queryCause(Criteria criteria,${entity.className} ${entity.lowerClassName}){\r\n    	 #{ADD_CAUSE_ON_CRITERIA_SMART_ADMIN}\r\n\r\n<c:if test=\"${not empty entity.parent}\">\r\n	${entity.parent.lowerClassName}Dao.queryCause(criteria,${entity.lowerClassName});\r\n</c:if>\r\n    }\r\n<c:if test=\"${entity.exportable==1 or entity.importable==1}\">\r\n	@Override\r\n	public List<${entity.className}> findAllList(${entity.className} ${entity.lowerClassName}, Page pageInfo) {\r\n		Criteria criteria = getSession().createCriteria(${entity.className}.class);\r\n		return executeCriteria(criteria, ${entity.lowerClassName}, pageInfo.getOrderField(),\r\n				pageInfo.getOrderDirection());\r\n	}\r\n	@SuppressWarnings(\"unchecked\")\r\n	/**\r\n	  * 执行criteria查询\r\n	  * @param criteria 需要执行的criteria\r\n	  * @param ${entity.lowerClassName} 查询对象\r\n	  * @param orderField 排序字段\r\n	  * @param orderDirection 排序方式\r\n	  * @return List<${entity.className}> ${entity.className}列表\r\n	  */\r\n	private List<${entity.className}> executeCriteria(Criteria criteria,\r\n			${entity.className} ${entity.lowerClassName}, String orderField, String orderDirection) {\r\n		queryCause(criteria, ${entity.lowerClassName});\r\n		if (orderField != null && !orderField.trim().equals(\"\")) {\r\n			if (orderDirection == null || orderDirection.trim().equals(\"asc\")) {\r\n				criteria.addOrder(Order.asc(orderField));\r\n			} else {\r\n				criteria.addOrder(Order.desc(orderField));\r\n			}\r\n		} else {\r\n			criteria.addOrder(Order.asc(\"${entity.idColumnName}\"));\r\n		}\r\n		return criteria.list();\r\n	}\r\n</c:if>\r\n	\r\n	@Override\r\n	public ${entity.className} findByVersion(Integer version) {\r\n		AuditReader reader = AuditReaderFactory.get(getSession());\r\n		AuditQueryCreator queryCreator2 = reader.createQuery();\r\n		AuditQuery query = queryCreator2.forEntitiesAtRevision(${entity.className}.class, version);\r\n		query.add(AuditEntity.revisionNumber().eq(version));\r\n		List list = query.getResultList();\r\n		if(list!=null && list.size()>0){\r\n			return (${entity.className})list.get(0);\r\n		}\r\n		return null;\r\n	}\r\n	\r\n	@Override\r\n	public List<${entity.className}> findByKey(Page pageInfo,String queryKey) {\r\n		Criteria criteria = getSession().createCriteria(${entity.className}.class).setFirstResult(pageInfo.getStartIndex()).setMaxResults(pageInfo.getNumPerPage()).add(Restrictions.like(\"${entity.queryProp}\", queryKey,MatchMode.ANYWHERE));\r\n		return criteria.list();\r\n	}\r\n\r\n	@Override\r\n	public Long findRowCount(String queryKey) {\r\n		Criteria criteria = getSession().createCriteria(${entity.className}.class)\r\n				.setProjection(Projections.rowCount())\r\n				.add(Restrictions.like(\"${entity.queryProp}\", queryKey,MatchMode.ANYWHERE));\r\n		return (Long) criteria.uniqueResult();\r\n	}\r\n\r\n	@SuppressWarnings(\"unchecked\")\r\n	@Override\r\n	public List<${entity.className}> findById(${entity.manageId eq \'username\' ? \'String\':\'Integer\'}[] ${entity.manageId eq \'username\' ? \'username\':\'id\'}s) {\r\n		Criteria criteria = getSession().createCriteria(${entity.className}.class)\r\n		.add(Restrictions.in(\"${entity.manageId eq \'username\' ? \'username\':\'id\'}\", ${entity.manageId eq \'username\' ? \'username\':\'id\'}s));\r\n		return criteria.list();\r\n	}\r\n\r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('58', 'Entity-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\n\r\nimport java.util.List;\r\nimport java.util.Set;\r\n\r\nimport javax.persistence.Column;\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.EnumType;\r\nimport javax.persistence.Enumerated;\r\nimport javax.persistence.FetchType;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.JoinColumn;\r\nimport javax.persistence.ManyToOne;\r\nimport javax.persistence.Table;\r\nimport javax.persistence.Transient;\r\nimport javax.persistence.UniqueConstraint;\r\n\r\nimport javax.persistence.Inheritance;\r\nimport javax.persistence.InheritanceType;\r\n\r\nimport javax.validation.constraints.NotNull;\r\n\r\nimport java.io.File;\r\nimport java.util.Date;\r\nimport javax.persistence.GenerationType;\r\n\r\nimport com.fasterxml.jackson.annotation.JsonIgnore;\r\nimport org.codehaus.jackson.annotate.JsonIgnoreProperties;\r\nimport org.codehaus.jackson.map.annotate.JsonSerialize;\r\nimport org.hibernate.annotations.Fetch;\r\nimport org.hibernate.annotations.FetchMode;\r\nimport org.hibernate.annotations.GenericGenerator;\r\nimport org.hibernate.validator.constraints.Length;\r\nimport org.hibernate.validator.constraints.NotBlank;\r\n\r\nimport com.cniaas.catfish.common.entity.CommonFile;\r\nimport com.cniaas.catfish.sys.entity.Department;\r\nimport com.cniaas.catfish.sys.entity.User;\r\nimport com.cniaas.catfish.ticket.configuration.entity.Category;\r\nimport com.cniaas.catfish.ticket.configuration.entity.Priority;\r\nimport com.cniaas.catfish.ticket.manage.entity.enumtype.OriginType;\r\nimport com.cniaas.catfish.ticket.manage.entity.enumtype.TicketStatus;\r\n\r\nimport com.cniaas.catfish.asset.configuration.entity.U;\r\nimport com.cniaas.catfish.asset.manage.software.entity.ConfigFile;\r\nimport com.cniaas.catfish.asset.configuration.entity.Location;\r\nimport com.cniaas.catfish.ticket.manage.entity.Ticket;\r\nimport com.cniaas.catfish.asset.configuration.entity.MemoryType;\r\nimport com.cniaas.catfish.asset.configuration.entity.Port;\r\n\r\nimport org.hibernate.annotations.Cascade;\r\nimport org.hibernate.annotations.CascadeType;\r\n\r\nimport org.hibernate.envers.Audited;\r\n\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\nimport com.cniaas.catfish.util.Md5Util;\r\n\r\n/**\r\n * ${entity.name}\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\n@Entity\r\n<c:if test=\"${entity.historyable==1}\">\r\n@Audited\r\n</c:if>\r\n<c:choose>\r\n<c:when test=\"${not empty entity.tableName}\">\r\n@Table(name=\"${entity.tableName}\")\r\n</c:when>\r\n<c:otherwise>\r\n@Table(name=\"t_${entity.lowerClassName}\")\r\n</c:otherwise>\r\n</c:choose>\r\n<c:if test=\"${not empty entity.children}\">\r\n@Inheritance(strategy = InheritanceType.JOINED)\r\n</c:if>\r\n@JsonIgnoreProperties( value={\"hibernateLazyInitializer\",\"handler\"})\r\npublic class ${entity.className}\r\n<c:if test=\"${not empty entity.parent}\">\r\n	extends ${entity.parent.fullClassName}\r\n</c:if>\r\n{\r\n<c:if test=\"${entity.idGenerateType==0 and empty entity.parent}\">\r\n/**唯一识别ID号 */\r\nprotected Integer id;\r\n	/**@param id 要设置的唯一标示号*/\r\npublic void setId(Integer id){this.id=id;}\r\n@Id\r\n@GeneratedValue(strategy=GenerationType.AUTO)\r\n@Column(columnDefinition=\"integer\")\r\n	/**@return Integer 唯一标示号*/\r\npublic Integer getId(){return this.id;}\r\n</c:if>\r\n\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n\r\n<c:if test=\"${p.noPersistence!=1}\">\r\n/**${p.name}*/\r\nprivate ${p.dataTypeName} ${p.propName};\r\n</c:if>\r\n\r\n<c:if test=\"${p.forQuery==1 and (p.dataType==2 or p.dataType==3 or p.dataType==4)}\">\r\n	/**${p.name}开始，用于查询*/\r\n \r\nprivate ${p.dataTypeName} ${p.propName}Start;\r\n	/**${p.name}结束，用于查询*/\r\n\r\nprivate ${p.dataTypeName} ${p.propName}End;\r\n</c:if>\r\n<c:if test=\"${p.dataType==6 and p.relationType==4}\">\r\n	/**${p.name}模糊查询条件*/\r\n \r\nprivate String ${p.propName}Query;\r\n</c:if>\r\n<c:if test=\"${p.dataType==6 and p.relationType==3}\">\r\n	/**${p.name}临时集合*/\r\nprivate List<${p.complexEntity.className}> ${p.propName}List;\r\n</c:if>\r\n</c:forEach>\r\n\r\n<c:if test=\"${entity.manageKeySupport==1 and empty entity.parent}\">\r\n	/**MD5管理ID*/\r\n	protected String manageKey;\r\n	\r\n	/**@return String MD5管理ID */\r\n	 \r\n	public String getManageKey() {\r\n		return manageKey;\r\n	}\r\n	\r\n	/**\r\n	 * @param manageKey 要设置的MD5管理ID \r\n	 */\r\n	public void setManageKey(String manageKey) {\r\n		this.manageKey = manageKey;\r\n	}\r\n</c:if>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${empty p.noPersistence or p.noPersistence==0}\">\r\n/**\r\n * @param ${p.propName} 要设置的${p.name}\r\n */\r\npublic void ${p.setterName}(${p.dataTypeName} ${p.propName}){\r\n    this.${p.propName} = ${p.propName};\r\n}\r\n<c:if test=\"${p.dataType==6 and p.relationType==4}\">\r\n/**\r\n * @param ${p.propName}Query 要设置的${p.name}模糊查询条件\r\n */\r\npublic void ${p.setterName}Query(String ${p.propName}Query){\r\n    this.${p.propName}Query = ${p.propName}Query;\r\n}\r\n</c:if>\r\n</c:if>\r\n\r\n/**\r\n * @return ${p.dataTypeName} ${p.name} \r\n */\r\n#{JPA_ANNOTATION_SMART_ADMIN}\r\npublic ${p.dataTypeName} ${p.getterName}(){\r\n    <c:choose>\r\n		<c:when test=\"${p.noPersistence==1}\">\r\n		return null;\r\n		</c:when>\r\n		<c:otherwise>\r\n		return this.${p.propName};	\r\n		</c:otherwise>\r\n	</c:choose>\r\n}\r\n<c:if test=\"${p.forQuery==1 and (p.dataType==2 or p.dataType==3 or p.dataType==4)}\">\r\n/**\r\n  * @param ${p.propName}Start 要设置的${p.name}开始日期\r\n  */\r\npublic void ${p.setterName}Start(${p.dataTypeName} ${p.propName}Start){\r\n    this.${p.propName}Start = ${p.propName}Start;\r\n}\r\n/**\r\n  * @return ${p.dataTypeName} ${p.name}开始日期\r\n  */\r\n@Transient\r\npublic ${p.dataTypeName} ${p.getterName}Start(){\r\n    return this.${p.propName}Start;\r\n}\r\n\r\n/**\r\n  * @param ${p.propName}End 要设置的${p.name}结束日期\r\n  */\r\npublic void ${p.setterName}End(${p.dataTypeName} ${p.propName}End){\r\n    this.${p.propName}End = ${p.propName}End;\r\n}\r\n/**\r\n  * @return ${p.dataTypeName} ${p.name}结束日期\r\n  */\r\n@Transient\r\npublic ${p.dataTypeName} ${p.getterName}End(){\r\n    return this.${p.propName}End;\r\n}\r\n</c:if>\r\n<c:if test=\"${p.dataType==6 and p.relationType==4}\">\r\n/**\r\n * @return  String ${p.name}模糊查询条件\r\n */\r\n@Transient\r\npublic String ${p.getterName}Query(){\r\n    return this.${p.propName}Query;\r\n}\r\n</c:if>\r\n<c:if test=\"${p.dataType==6 and p.relationType==3}\">\r\n	/**\r\n	 * @return  ${p.complexEntity.className} 获取${p.getterName}对应的临时集合\r\n	 */\r\n	@Transient\r\n	public List<${p.complexEntity.className}> ${p.getterName}List() {\r\n		return ${p.propName}List;\r\n	}\r\n	/**\r\n	 * @param  ${p.propName}List 要设置的${p.name}对应的临时集合\r\n	 */\r\n	public void ${p.setterName}List(List<${p.complexEntity.className}> ${p.propName}List) {\r\n		this.${p.propName}List = ${p.propName}List;\r\n	}\r\n	/**\r\n	*  转换${p.propName}对应的临时集合到Set中<br/>\r\n	*  方法通过检查rowGuard的值，来过滤掉\"空对象\"\r\n	 * @param HttpServletRequest request\r\n	 */\r\n	public void ${p.propName}ListToSet(HttpServletRequest request){\r\n		if(${p.propName}==null){\r\n			${p.propName} = new LinkedHashSet<${p.complexEntity.className}>();\r\n		}else{\r\n			${p.propName}.clear();\r\n		}\r\n		if(${p.propName}List!=null){\r\n			for(int i = 0;i<${p.propName}List.size();i++){\r\n				String rowGuard = request.getParameter(\"rowGuard\" + i);\r\n				if(rowGuard==null || rowGuard.trim().equals(\"\")){\r\n					continue;\r\n				}\r\n				${p.complexEntity.className} tmp${p.complexEntity.lowerClassName} = ${p.propName}List.get(i);\r\n				tmp${p.complexEntity.lowerClassName}.set${fn:toUpperCase(fn:substring(p.setKeyCoumn, 0, 1))}${fn:substring(p.setKeyCoumn, 1,fn:length(p.setKeyCoumn))}(this);\r\n				tmp${p.complexEntity.lowerClassName}.setManageKey(Md5Util.getManageKey());\r\n				${p.propName}.add(tmp${p.complexEntity.lowerClassName});\r\n			}\r\n		}\r\n	}\r\n</c:if>\r\n</c:forEach>\r\n\r\n	@Override\r\n	public boolean equals(Object obj) {\r\n		if (this == obj)\r\n			return true;\r\n		if (obj == null)\r\n			return false;\r\n		${entity.className} other = null;\r\n		try{\r\n			other = (${entity.className}) obj;\r\n		}catch(Exception e){\r\n			return false;\r\n		}\r\n		if (manageKey == null) {\r\n			if (other.getManageKey() != null)\r\n				return false;\r\n		} else if (!manageKey.equals(other.getManageKey()))\r\n			return false;\r\n		return true;\r\n	}\r\n\r\n	@Override\r\n	public int hashCode() {\r\n		final int prime = 31;\r\n		int result = 1;\r\n		result = prime * result + ((manageKey == null) ? 0 : manageKey.hashCode());\r\n		return result;\r\n	}\r\n\r\n	@Override\r\n	public String toString() {\r\n		return \"${entity.className} [manageKey=\" + manageKey + \"]\";\r\n	}\r\n	<c:if test=\"${empty entity.parent and entity.historyable eq 1}\">\r\n	/**版本号*/\r\n	protected int version;\r\n	\r\n	/**\r\n	  * @return int 版本号\r\n	  */\r\n	@Column(name = \"_version\",columnDefinition=\"int(11) default 0\")\r\n	public int getVersion() {\r\n		return version;\r\n	}\r\n\r\n	/**\r\n	  * @param version 要设置的版本号\r\n	  */\r\n	public void setVersion(int version) {\r\n		this.version = version;\r\n	}\r\n\r\n	/**\r\n	  * 版本号自增1\r\n	  */\r\n	public void incrementVersion() {\r\n		version++;\r\n	}\r\n	</c:if>\r\n\r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('59', 'Enum类型-SmartAdmin', 'package ${entity.currentFileFullPackage};\r\nimport java.util.LinkedHashMap;\r\nimport java.util.Map;\r\n\r\n/**\r\n * ${entity.name}<br>  ${entity.str2Enum}\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\npublic enum ${entity.className}{\r\n    ${entity.str2Enum}\r\n /**  description 描述信息*/\r\n    private String description;\r\n    private ${entity.className}(String description){this.description = description;}\r\n\r\n    /**\r\n	  *  获取描述信息\r\n      * @return String 描述信息\r\n	  */\r\n    public String getDescription(){return description;}\r\n\r\n    /**\r\n	  * 枚举对象-描述信息 键值对\r\n      * @return Map<${entity.className},String> 枚举对象-描述信息 键值对\r\n	  */\r\n    public final static Map<${entity.className},String> ${entity.lowerClassName}Map = new LinkedHashMap<${entity.className},String>();\r\n    static{\r\n        for(${entity.className} s : ${entity.className}.values()){\r\n          ${entity.lowerClassName}Map.put(s,s.getDescription());  \r\n        }\r\n    }\r\n}', '5', '2');
INSERT INTO `t_template` VALUES ('60', 'PropertyEditor-SmartAdmin', 'package ${entity.fullPackageName}.propertyeditor;\r\nimport java.beans.PropertyEditorSupport;\r\nimport ${entity.fullClassName};\r\n\r\nimport com.cniaas.catfish.sys.entity.enumtype.UseStatus;\r\n\r\n/**\r\n * ${entity.name}编辑器\r\n * @author NOVOTS\r\n * @version Version 1.0\r\n */\r\npublic class ${entity.className}Editor extends PropertyEditorSupport{\r\n    public I${entity.className}Service ${entity.lowerClassName}Service;\r\n    public ${entity.className}Editor(I${entity.className}Service ${entity.lowerClassName}Service){\r\n        this.${entity.lowerClassName}Service = ${entity.lowerClassName}Service;\r\n    }\r\n    public String getAsText(){\r\n        ${entity.className} ${entity.lowerClassName} = (${entity.className})getValue();\r\n        if(${entity.lowerClassName}==null){\r\n            return \"\";\r\n        }\r\n        return String.valueOf(${entity.lowerClassName}.${entity.idCoumunGetterName}());\r\n    }\r\n    <c:set value=\"${entity.idColumnProp}\" var=\"idprop\"/>\r\n    public void setAsText(String key)throws IllegalArgumentException {\r\n        ${entity.className} ${entity.lowerClassName} = null;\r\n	\r\n${entity.lowerClassName} = ${entity.lowerClassName}Service.getEntityByProperty(${entity.className}.class,\"manageKey\",key);\r\nif(${entity.lowerClassName}==null){\r\n            Integer integerId = null;\r\n            try {integerId = Integer.parseInt(key);} catch (Exception e) {}\r\n            ${entity.lowerClassName} = ${entity.lowerClassName}Service.getById(${entity.className}.class,integerId);\r\n}\r\nif(key!=null && !key.trim().equals(\"\") && ${entity.lowerClassName}==null){\r\n${entity.lowerClassName}=new ${entity.className}();\r\n}\r\n        setValue(${entity.lowerClassName});\r\n    }\r\n}', '5', '2');
