r\n							<c:when test=\"${p.dataType==2 or p.dataType==3}\">\r\n								<form:input path=\"${p.propName}\"\r\n									id=\"${entity.lowerClassName}.${p.propName}\" type=\"text\"\r\n									cssClass=\"textInput ${(empty p.canNull or p.canNull==0)?\'required\':\'\'}${p.dataType==2?\' digits\':\' number\'}\" />\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==1}\">\r\n								<c:choose>\r\n									<c:when test=\"${p.isTextArea==1}\">\r\n										<form:textarea path=\"${p.propName}\"\r\n											id=\"${entity.lowerClassName}.${p.propName}\" cols=\"${p.col}\"\r\n											rows=\"${p.row}\"\r\n											cssClass=\"${(empty p.canNull or p.canNull==0)?\'required\':\'\'}\"\r\n											<c:if test=\"${p.isTextStringType==0}\">maxlength=\"${p.length}\"</c:if> />\r\n									</c:when>\r\n									<c:otherwise>\r\n										<form:input path=\"${p.propName}\"\r\n											id=\"${entity.lowerClassName}.${p.propName}\"\r\n											cssClass=\"textInput ${(empty p.canNull or p.canNull==0)?\'required\':\'\'}\"\r\n											maxlength=\"${p.length}\" />\r\n									</c:otherwise>\r\n								</c:choose>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==4}\">\r\n								<input name=\"${p.propName}\" id=\"${entity.lowerClassName}.${p.propName}\" onclick=\"WdatePicker({dateFmt:\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\'});\"\r\n								value=\"[_LT]fmt:formatDate pattern=\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\' value=\'\\\\${${entity.lowerClassName}.${p.propName}}\'/>\" class=\"date_${p.canNull==1?\'not_required textInput valid\':\'required required textInput valid\'}\"/>\r\n							</c:when>\r\n							\r\n							<c:when test=\"${p.dataType==6}\">\r\n								<form:select id=\"${p.propName}\" path=\"${p.propName}\" cssClass=\"comboxed${p.canNull==1?\'\':\' required\'}\">\r\n								<option value=\"\">请选择</option>\r\n								<form:options  items=\"\\\\${${p.complexEntity.lowerClassName}List}\" itemValue=\"${p.complexEntity.idColumnName}\" itemLabel=\"${p.valuePath}\" cssClass=\"comboxed\"/>\r\n								</form:select>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==7}\">\r\n								<form:select id=\"${p.propName}\" path=\"${p.propName}\" cssClass=\"${p.canNull==1?\'\':\'required\'} comboxed\">\r\n								<option value=\"\">请选择</option>\r\n									<form:options  items=\"\\\\${${p.complexEntity.lowerClassName}Map}\"/>\r\n								</form:select>\r\n							</c:when>\r\n						</c:choose>\r\n						<c:if test=\"${(empty p.canNull or p.canNull==0) and (p.dataType==6 or p.dataType==7)}\">\r\n							<font style=\'color:red\'>*</font>\r\n						</c:if>\r\n				</p>\r\n			</c:otherwise>\r\n		</c:choose>\r\n		</c:if>\r\n</c:forEach>', null);
INSERT INTO `t_varlabel` VALUES ('25', 'QUERY_TABLE', '列表页查询栏输入框', '1', '<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n	<c:if test=\"${p.onlyRelationship!=1  and p.forQuery==1  and (empty p.noPersistence or p.noPersistence==0)}\">\r\n\r\n		<c:choose>\r\n			<c:when test=\"${p.isTotalRow==1}\">\r\n				<dl class=\"nowrap\">\r\n					<dt>\r\n						${p.name}：\r\n					</dt>\r\n					<dd>\r\n						<c:choose>\r\n							<c:when test=\"${p.dataType==2 or p.dataType==3}\">\r\n								<form:input path=\"${p.propName}\"\r\n									id=\"${entity.lowerClassName}.${p.propName}\" type=\"text\"\r\n									cssClass=\"textInput ${p.dataType==2?\'digits\':\'number\'}\" size=\"12\"/>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==1}\">\r\n								<c:choose>\r\n									<c:when test=\"${p.isTextArea==1}\">\r\n										<form:textarea path=\"${p.propName}\"\r\n											id=\"${entity.lowerClassName}.${p.propName}\" cols=\"${p.col}\"\r\n											rows=\"${p.row}\"\r\n											<c:if test=\"${p.isTextStringType==0}\">maxlength=\"${p.length}\"</c:if> />\r\n									</c:when>\r\n									<c:otherwise>\r\n										<form:input path=\"${p.propName}\"\r\n											id=\"${entity.lowerClassName}.${p.propName}\"\r\n											cssClass=\"textInput\"\r\n											maxlength=\"${p.length}\"  size=\"12\"/>\r\n									</c:otherwise>\r\n								</c:choose>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==4}\">\r\n								<input name=\"${p.propName}Start\" id=\"${entity.lowerClassName}.${p.propName}\" onclick=\"WdatePicker({dateFmt:\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\'});\"\r\n								value=\"[_LT]fmt:formatDate pattern=\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\' value=\'\\\\${${entity.lowerClassName}.${p.propName}Start}\'/>\" class=\"date_not_required textInput valid\"  size=\"12\"/>\r\n								至\r\n								<input name=\"${p.propName}End\" id=\"${entity.lowerClassName}.${p.propName}\" onclick=\"WdatePicker({dateFmt:\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\'});\"\r\n								value=\"[_LT]fmt:formatDate pattern=\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\' value=\'\\\\${${entity.lowerClassName}.${p.propName}End}\'/>\" class=\"date_not_required textInput valid\"  size=\"12\"/>\r\n							</c:when>\r\n							\r\n							<c:when test=\"${p.dataType==6}\">\r\n								<form:select id=\"${p.propName}\" path=\"${p.propName}\" cssClass=\"comboxed\">\r\n								<option value=\"\">请选择</option>\r\n								<form:options  items=\"\\\\${${p.complexEntity.lowerClassName}List}\" itemValue=\"${p.complexEntity.idColumnName}\" itemLabel=\"${p.valuePath}\" cssClass=\"comboxed\"  size=\"12\"/>\r\n								</form:select>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==7}\">\r\n								<form:select id=\"${p.propName}\" path=\"${p.propName}\" cssClass=\"comboxed\">\r\n								<option value=\"\">请选择</option>\r\n									<form:options  items=\"\\\\${${prop.complexEntity.lowerClassName}Map}\"/>\r\n								</form:select>\r\n							</c:when>\r\n						</c:choose>\r\n\r\n					</dd>\r\n				</dl>\r\n			</c:when>\r\n			<c:otherwise>\r\n					<li>\r\n					<label>\r\n						${p.name}：\r\n					</label>\r\n					\r\n						<c:choose>\r\n							<c:when test=\"${p.dataType==2 or p.dataType==3}\">\r\n								<form:input path=\"${p.propName}\"\r\n									id=\"${entity.lowerClassName}.${p.propName}\" type=\"text\"\r\n									cssClass=\"textInput${p.dataType==2?\'digits\':\'number\'}\" size=\"12\"/>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==1}\">\r\n								<c:choose>\r\n									<c:when test=\"${p.isTextArea==1}\">\r\n										<form:textarea path=\"${p.propName}\"\r\n											id=\"${entity.lowerClassName}.${p.propName}\" cols=\"${p.col}\"\r\n											rows=\"${p.row}\"\r\n											<c:if test=\"${p.isTextStringType==0}\">maxlength=\"${p.length}\"</c:if> />\r\n									</c:when>\r\n									<c:otherwise>\r\n										<form:input path=\"${p.propName}\"\r\n											id=\"${entity.lowerClassName}.${p.propName}\"\r\n											cssClass=\"textInput\"\r\n											maxlength=\"${p.length}\"  size=\"12\"/>\r\n									</c:otherwise>\r\n								</c:choose>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==4}\">\r\n								<input name=\"${p.propName}Start\" id=\"${entity.lowerClassName}.${p.propName}\" onclick=\"WdatePicker({dateFmt:\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\'});\"\r\n								value=\"[_LT]fmt:formatDate pattern=\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\' value=\'\\\\${${entity.lowerClassName}.${p.propName}Start}\'/>\" class=\"date_not_required textInput valid\"  size=\"12\"/>\r\n								至\r\n								<input name=\"${p.propName}End\" id=\"${entity.lowerClassName}.${p.propName}\" onclick=\"WdatePicker({dateFmt:\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\'});\"\r\n								value=\"[_LT]fmt:formatDate pattern=\'\\\\${applicationScope.EN_YEAR_MONTH_DAY}\' value=\'\\\\${${entity.lowerClassName}.${p.propName}End}\'/>\" class=\"date_not_required textInput valid\"  size=\"12\"/>\r\n							</c:when>\r\n							\r\n							<c:when test=\"${p.dataType==6}\">\r\n								<form:select id=\"${p.propName}\" path=\"${p.propName}\" cssClass=\"comboxed\">\r\n								<option value=\"\">请选择</option>\r\n								<form:options  items=\"\\\\${${p.complexEntity.lowerClassName}List}\" itemValue=\"${p.complexEntity.idColumnName}\" itemLabel=\"${p.valuePath}\" cssClass=\"comboxed\"/>\r\n								</form:select>\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==7}\">\r\n								<form:select id=\"${p.propName}\" path=\"${p.propName}\" cssClass=\"comboxed\">\r\n								<option value=\"\">请选择</option>\r\n									<form:options  items=\"\\\\${${p.complexEntity.lowerClassName}Map}\"/>\r\n								</form:select>\r\n							</c:when>\r\n						</c:choose>\r\n				</li>\r\n			</c:otherwise>\r\n		</c:choose>\r\n\r\n	</c:if>\r\n</c:forEach>', null);
INSERT INTO `t_varlabel` VALUES ('26', 'HEAD_TDS', '列表页面Table表头行', '1', '<!--<th width=\"30\" align=\"center\"><input type=\"checkbox\" group=\"${entity.idColumnName}s\" class=\"checkboxCtrl\"></th>-->\r\n<c:if test=\"${entity.idGenerateType==0}\">\r\n	<th orderField=\"id\" [_LT]c:if test=\"[_D]{pageBean.orderField==\'${entity.idColumnName}\'}\">class=\"[_D]{pageBean.orderDirection}\"[_LT]/c:if[_GT]>编号</th>\r\n</c:if>\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.display==1}\">\r\n		<c:if test=\"${p.dataType!=6 or (p.relationType != 2 and p.relationType != 3)}\">\r\n		<th orderField=\"${p.propName}\" [_LT]c:if test=\"[_D]{pageBean.orderField==\'${p.propName}\'}\">class=\"[_D]{pageBean.orderDirection}\"[_LT]/c:if>>${p.name}</th>\r\n		</c:if>\r\n</c:if>\r\n</c:forEach>', null);
INSERT INTO `t_varlabel` VALUES ('27', 'BODY_TDS', '列表页Table行的格', '1', '<!--<td align=\"center\"><input name=\"${entity.idColumnName}s\" type=\"checkbox\" value=\"[_D]{${entity.lowerClassName}.${entity.idColumnName}}\"/>-->\r\n<c:set var=\"tempBeanName\" value=\"temp${entity.lowerClassName}\"/>\r\n<c:if test=\"${entity.idGenerateType==0}\">\r\n	<td>[_D]{${tempBeanName}.${entity.idColumnName}}</td>\r\n</c:if>\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.display==1}\">\r\n	<c:choose>\r\n		<c:when test=\"${p.isAttachement==1}\">\r\n			<td>\r\n				<c:choose>\r\n					<c:when test=\"${p.isPicture==1 and p.attachementDisplayType eq \'img\'}\">\r\n						<div class=\"nailthumb-container square-thumb\" style=\"border:1px solid gray;\">\r\n							[_LT]c:choose[_GT]\r\n							[_LT]c:when test=\"[_D]{not empty temp${entity.lowerClassName}.${p.propName}}\">\r\n							<a href=\"${basePath}filee/photoView.do?manageKey=[_D]{temp${entity.lowerClassName}.${p.propName}.manageKey}\" target=\"_blank\" title=\"点击查看原图\"><img src=\"${basePath}filee/photoView.do?manageKey=[_D]{temp${entity.lowerClassName}.${p.propName}.manageKey}\" border=\"0\" /></a>\r\n							[_LT]/c:when[_GT]\r\n							[_LT]c:otherwise[_GT]\r\n							--\r\n							[_LT]/c:otherwise[_GT]\r\n							[_LT]/c:choose>\r\n						</div>\r\n					</c:when>\r\n					<c:otherwise>\r\n						<a class=\"btnDownLoad\" href=\"filee/downloadFile.do?key=[_D]{temp${entity.lowerClassName}.${p.propName}.manageKey}\" target=\"dwzExport\" method=\"GET\"><span></span></a>\r\n					</c:otherwise>\r\n				</c:choose>\r\n			</td>\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==6}\">\r\n			<c:if test=\"${p.relationType != 2 and p.relationType != 3}\">\r\n			<td>[_D]{${tempBeanName}.${p.propName}<c:if test=\"${p.relationType != 2 and p.relationType != 3  and (not empty p.valuePath)}\">.${p.valuePath}</c:if>}</td>\r\n			</c:if>\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==4}\">\r\n			<td>[_LT]fmt:formatDate value=\"[_D]{${tempBeanName}.${p.propName}}\" pattern=\"${p.timeFormat}\"/></td>\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==5}\">\r\n		[暂不支持数据字典类型]\r\n		</c:when>\r\n		<c:otherwise>\r\n		<td>\r\n		<c:if test=\"${p.dataType==1 or p.dataType==2 or p.dataType==3}\">\r\n		[_D]{${tempBeanName}.${p.propName}}\r\n		</c:if>\r\n		<c:if test=\"${p.dataType==7}\">\r\n		[_D]{${p.complexEntity.lowerClassName}Map[${tempBeanName}.${p.propName}]}\r\n		</c:if>\r\n		</td>\r\n		</c:otherwise>\r\n	</c:choose>\r\n</c:if>\r\n</c:forEach>', null);
