INSERT INTO `t_varlabel` VALUES ('35', 'COMPLEX_LIST_PREPARE', '准备复杂管理关系下拉列表的集合', '1', '<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.relationType!=2 and p.relationType!=3}\">\r\n<c:if test=\"${p.dataType==6}\">\r\n\r\n	<c:choose>\r\n		<c:when test=\"${p.complexEntity.hasStatusProp}\">\r\n		\r\n			<c:choose>             \r\n				<c:when test=\"${p.complexEntity.hasSortProp}\">\r\n					List<${p.dataTypeName}> ${p.complexEntity.lowerClassName}List = ${p.complexEntity.lowerClassName}Service.getByProperties(${p.complexEntity.className}.class,new String[]{\"status\"},new Object[]{IsActive.Yes},null,null);\r\n				</c:when>\r\n				<c:otherwise>\r\n				List<${p.dataTypeName}> ${p.complexEntity.lowerClassName}List = ${p.complexEntity.lowerClassName}Service.getByProperties(${p.complexEntity.className}.class,new String[]{\"status\"},new Object[]{IsActive.Yes},null,null);\r\n				</c:otherwise>\r\n			</c:choose>\r\n			\r\n			\r\n		</c:when>\r\n		<c:otherwise>\r\n		<c:choose>\r\n				<c:when test=\"${p.complexEntity.hasSortProp}\">\r\n					List<${p.dataTypeName}> ${p.complexEntity.lowerClassName}List = ${p.complexEntity.lowerClassName}Service.getByProperties(${p.complexEntity.className}.class,null,null,null,null);\r\n				</c:when>\r\n				<c:otherwise>\r\n				List<${p.dataTypeName}> ${p.complexEntity.lowerClassName}List = ${p.complexEntity.lowerClassName}Service.getByProperties(${p.complexEntity.className}.class,null,null,null,null);\r\n				</c:otherwise>\r\n			</c:choose>\r\n		</c:otherwise>\r\n	</c:choose>\r\n	request.setAttribute(\"${p.complexEntity.lowerClassName}List\",${p.complexEntity.lowerClassName}List);\r\n\r\n\r\n</c:if>\r\n<c:if test=\"${p.dataType==7}\">\r\nrequest.setAttribute(\"${p.complexEntity.lowerClassName}Map\",${p.complexEntity.className}.${p.complexEntity.lowerClassName}Map);\r\n</c:if>\r\n</c:if>\r\n</c:forEach>', null);
