INSERT INTO `t_varlabel` VALUES ('42', 'VALIDATE_ANNOTATION', '属性验证注解', '1', '<c:if test=\"${not(p.dataType==4 or p.dataType==7 or (p.dataType==6 and (p.relationType==2 or p.relationType==3)))}\">\r\n    <c:if test=\"${p.canNull==0}\">\r\n        <c:choose>\r\n            <c:when test=\"${p.dataType==1}\">\r\n                @NotBlank(message=\"${p.name}不能为空\")\r\n            </c:when>\r\n            <c:when test=\"${p.dataType==6 and (p.relationType==2 or p.relationType==3)}\">\r\n                @NotBlank(message=\"${p.name}不能为空\")\r\n            </c:when>\r\n            <c:otherwise>\r\n                @NotNull(message=\"${p.name}不能为空\")\r\n            </c:otherwise>\r\n        </c:choose>\r\n    </c:if>\r\n    <c:if test=\"${p.dataType==1} and p.length>0 and p.isTextStringType==0\">\r\n        @Length(min=0,max=${p.length},message=\"${p.name}长度不能超过${p.length}\")\r\n    </c:if>\r\n</c:if>', null);
INSERT INTO `t_varlabel` VALUES ('43', 'JPA_ANNOTATION', '生成jpa属性注解', '1', '<c:choose>\r\n	<c:when test=\"${p.noPersistence==1}\">\r\n		@Transient\r\n	</c:when>\r\n	<c:otherwise>\r\n\r\n<c:if test=\"${p.entity.type==0 and p.entity.idGenerateType==1 and p.isId==1}\">\r\n@Id\r\n@GeneratedValue(generator=\"${p.propName}_generator\")\r\n@GenericGenerator(name=\"${p.propName}_generator\",strategy=\"assigned\")\r\n</c:if>\r\n\r\n<c:if test=\"${p.dataType!=6}\">\r\n@Column(name=\"${p.columnOrPropName}\"<c:if test=\"${p.dataType==1}\"><c:if test=\"${p.length>0}\">,length=${p.length}</c:if><c:if test=\"${p.isTextStringType==1}\">,columnDefinition=\"text\"</c:if></c:if>,nullable=${p.canNull==1}<c:if test=\"${p.dataType==7}\">,columnDefinition=\"enum(${p.complexEntity.enumDefinitionSQL})\"</c:if>)<c:if test=\"${p.dataType==7}\">\r\n@Enumerated(EnumType.STRING)\r\n</c:if>\r\n</c:if>\r\n\r\n<c:if test=\"${p.dataType==6}\">\r\n<c:choose>\r\n   <c:when test=\"${p.relationType==3}\">\r\n@OneToMany(targetEntity=${p.complexEntity.fullClassName}.class,mappedBy=\"${p.setKeyCoumn}\",fetch=FetchType.LAZY)\r\n   </c:when>\r\n   <c:when test=\"${p.relationType==4}\">\r\n@ManyToOne(targetEntity=${p.complexEntity.fullClassName}.class,fetch=FetchType.LAZY)\r\n@JoinColumn(name=\"${p.columnOrPropName}\",nullable=${p.canNull==1})\r\n@Fetch(FetchMode.SELECT)\r\n   </c:when>\r\n   <c:when test=\"${p.relationType==1}\">\r\n      \r\n   </c:when>\r\n   <c:when test=\"${p.relationType==2}\">\r\n@ManyToMany(targetEntity=${p.complexEntity.fullClassName}.class)\r\n@JoinTable(name=\"${p.middletable}\",joinColumns={@JoinColumn(name=\"${p.entity.idColumnNameForForeignUse}\")},inverseJoinColumns={@JoinColumn(name=\"${p.complexEntity.idColumnNameForForeignUse}\")})\r\n   </c:when>\r\n</c:choose>\r\n@JsonIgnore\r\n</c:if>\r\n</c:otherwise>\r\n</c:choose>', null);
