INSERT INTO `t_varlabel` VALUES ('72', 'MANY_TO_MANY_DATATYPE_FROMPROP', '多对对关系管理form表单、一对多列表显示', '1', '<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n	<c:choose>\r\n		<c:when test=\"${p.dataType==6 and p.relationType==3}\">\r\n			[_LT]!-- ${p.propName}:one to many prop tab start -->\r\n				<div style=\"display: none;height:90%;width:100%;\" layoutH=\"98\" >\r\n					<div id=\"${p.propName}List\">\r\n					</div>\r\n					 \r\n					<script type=\"text/javascript\">\r\n					<!--\r\n					$(function(){\r\n						var params = {manageKey:\"[_LT]c:forEach items=\"\\\\${tempBean.${p.propName}}\" var=\"s\" varStatus=\"status\">\\\\${status.first ? \'\':\',\'}\\\\${s.manageKey}[_LT]/c:forEach>\"};\r\n						refresh${p.propName}List(params);\r\n						\r\n					});\r\n					function refresh${p.propName}List(args){\r\n						args = args || {};\r\n						args.htmlType=\'yes\';\r\n						args.keyName=\'${p.propName}Keys\';\r\n						args.showCheckBox=\'false\';\r\n						args.from=\'${entity.lowerClassName}_${p.propName}\';\r\n						$.post(basePath + \"admin/${p.complexEntity.lowerClassName}/selectList.do\",args,function(d){\r\n							try{\r\n								var currentTarget = getCurrentTarget();\r\n								$(\"#${p.propName}List\",currentTarget).html(d);\r\n								$(\"table.table\", currentTarget).jTable();\r\n								initUI($(\"#${p.propName}List\",currentTarget).parent());\r\n								$(\":button.checkboxCtrl, :checkbox.checkboxCtrl\", currentTarget).checkboxCtrl(currentTarget);\r\n							}catch(e){\r\n								alert(e);\r\n							}\r\n						});\r\n					}\r\n					//-->\r\n					</script>\r\n				</div>\r\n				[_LT]!-- ${p.propName}:!-- one to many prop tab end -->\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==6 and p.relationType==2}\">\r\n			<c:if test=\"${p.maintainOneToManyRelation eq 1}\">\r\n				[_LT]!-- ${p.propName}:many to many prop tab start -->\r\n				<div style=\"display: none;height:90%;width:100%;\" layoutH=\"98\" >\r\n					<div id=\"${p.propName}List\">\r\n					</div>\r\n					 \r\n					 <div class=\"buttonActive\">\r\n						<div class=\"buttonContent\">\r\n							<button type=\"button\" id=\"removeItem_${p.propName}\" title=\"[_LT]spring:message code=\"base.common.form.removeselected\"/>\">[_LT]spring:message code=\"base.common.form.removeselected\"/></button>\r\n						</div>\r\n					</div>\r\n					\r\n					<n:pv url=\"admin/${p.complexEntity.lowerClassName}/selectList.do\">\r\n					<a class=\"btnMultiLook\" href=\"\\\\${basePath}admin/${p.complexEntity.lowerClassName}/selectList.do?multiSelect=true&targetType=dialog&rel=${entity.lowerClassName}_${p.propName}_selectDialog\" \r\n					   hiddenid=\"${entity.lowerClassName}\" title=\'[_LT]spring:message code=\"base.function.add\"/>[_LT]spring:message code=\"props.${entity.fullClassName}.${p.propName}\"/>\' \r\n					   width=\"900\" height=\"600\" lookupgroup=\"${p.propName}\" rel=\"${entity.lowerClassName}_${p.propName}_selectDialog\"\r\n					   propmap=\"one2manySelect\" callBack=\"refresh${p.propName}List\">\r\n						<div class=\"buttonActive\">\r\n							<div class=\"buttonContent\">\r\n								<button type=\"button\" id=\"select${p.propName}\">[_LT]spring:message code=\"base.function.add\"/>[_LT]spring:message code=\"props.${entity.fullClassName}.${p.propName}\"/>...</button>\r\n							</div>\r\n						</div>\r\n					</a>\r\n					</n:pv>\r\n					<script type=\"text/javascript\">\r\n					<!--\r\n					$(function(){\r\n						$(\"#removeItem_${p.propName}\",getCurrentTarget()).click(function(){\r\n							var manageKey = [];\r\n							args = {};\r\n							//args.from=\'${entity.lowerClassName}_${p.propName}\';\r\n							//args.remove=\'yes\';\r\n							$(\"input[name=${p.propName}Keys]:checked\",getCurrentTarget()).each(function(i){\r\n								var tr = $(this).parents(\"tr\").first();\r\n								$(this).css(\'visibility\',false);\r\n								tr.remove();\r\n								//args.manageKey = $(this).val();\r\n								//$.post(basePath + \"admin/${p.complexEntity.lowerClassName}/selectList.do\",args,function(d){\r\n								//	//...\r\n								//});\r\n							});\r\n						});\r\n						\r\n						var params = {manageKey:\"[_LT]c:forEach items=\"\\\\${tempBean.${p.propName}}\" var=\"s\" varStatus=\"status\">\\\\${status.first ? \'\':\',\'}\\\\${s.manageKey}[_LT]/c:forEach>\"};\r\n						refresh${p.propName}List(params);\r\n						\r\n					});\r\n					function refresh${p.propName}List(args){\r\n						args = args || {};\r\n						\r\n						\r\n						var manageKeys = \'\';\r\n						$(\"input[name=${p.propName}Keys][type=hidden]\",getCurrentTarget()).each(function(i){\r\n							if($(this).val()){\r\n								if(manageKeys==\'\'){\r\n									manageKeys+= $(this).val();\r\n								}else{\r\n									manageKeys+= \",\"+$(this).val();\r\n								}\r\n							}\r\n						});\r\n						args[\"manageKey\"]= (args[\"manageKey\"] && args[\"manageKey\"]!=\'\')? (manageKeys+\",\" +args[\"manageKey\"]) : manageKeys;\r\n						\r\n						args.htmlType=\'yes\';\r\n						args.keyName=\'${p.propName}Keys\';\r\n						args.from=\'${entity.lowerClassName}_${p.propName}\';\r\n						$.post(basePath + \"admin/${p.complexEntity.lowerClassName}/selectList.do\",args,function(d){\r\n							try{\r\n								var currentTarget = getCurrentTarget();\r\n								$(\"#${p.propName}List\",currentTarget).html(d);\r\n								$(\"table.table\", currentTarget).jTable();\r\n								initUI($(\"#${p.propName}List\",currentTarget).parent());\r\n								$(\":button.checkboxCtrl, :checkbox.checkboxCtrl\", currentTarget).checkboxCtrl(currentTarget);\r\n							}catch(e){\r\n								alert(e);\r\n							}\r\n						});\r\n					}\r\n					//-->\r\n					</script>\r\n				</div>\r\n				[_LT]!-- ${p.propName}:!-- many to many prop tab end -->\r\n		</c:if>\r\n		</c:when>\r\n	</c:choose>\r\n	\r\n</c:forEach>', null);
