INSERT INTO `t_varlabel` VALUES ('81', 'MANY_TO_MANY_DATATYPE_FROMPROP_VIEW', '多对多、一对多关联关系查看', '1', '<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n	<c:choose>\r\n		<c:when test=\"${p.dataType==6 and p.relationType==3}\">\r\n			[_LT]!-- ${p.propName}:one to many prop tab start -->\r\n				<div style=\"display: none;height:90%;width:100%;\" layoutH=\"98\" >\r\n					<div id=\"${p.propName}List\">\r\n					</div>\r\n					 \r\n					<script type=\"text/javascript\">\r\n					<!--\r\n					$(function(){\r\n						var params = {manageKey:\"[_LT]c:forEach items=\"\\\\${tempBean.${p.propName}}\" var=\"s\" varStatus=\"status\">\\\\${status.first ? \'\':\',\'}\\\\${s.manageKey}[_LT]/c:forEach>\"};\r\n						refresh${p.propName}List(params);\r\n						\r\n					});\r\n					function refresh${p.propName}List(args){\r\n						args = args || {};\r\n						args.htmlType=\'yes\';\r\n						args.keyName=\'${p.propName}Keys\';\r\n						args.showCheckBox=\'false\';\r\n						args.from=\'${entity.lowerClassName}_${p.propName}\';\r\n						$.post(basePath + \"admin/${p.complexEntity.lowerClassName}/selectList.do\",args,function(d){\r\n							try{\r\n								var currentTarget = getCurrentTarget();\r\n								$(\"#${p.propName}List\",currentTarget).html(d);\r\n								$(\"table.table\", currentTarget).jTable();\r\n								initUI($(\"#${p.propName}List\",currentTarget).parent());\r\n								$(\":button.checkboxCtrl, :checkbox.checkboxCtrl\", currentTarget).checkboxCtrl(currentTarget);\r\n							}catch(e){\r\n								alert(e);\r\n							}\r\n						});\r\n					}\r\n					//-->\r\n					</script>\r\n				</div>\r\n				[_LT]!-- ${p.propName}:!-- one to many prop tab end -->\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==6 and p.relationType==2}\">\r\n			<c:if test=\"${p.maintainOneToManyRelation eq 1}\">\r\n				[_LT]!-- ${p.propName}:many to many prop tab start -->\r\n				<div style=\"display: none;height:90%;width:100%;\" layoutH=\"98\" >\r\n					<div id=\"${p.propName}List\">\r\n					</div>\r\n					 \r\n					 \r\n					\r\n					\r\n					<script type=\"text/javascript\">\r\n					<!--\r\n					$(function(){\r\n						var params = {manageKey:\"[_LT]c:forEach items=\"\\\\${tempBean.${p.propName}}\" var=\"s\" varStatus=\"status\">\\\\${status.first ? \'\':\',\'}\\\\${s.manageKey}[_LT]/c:forEach>\"};\r\n						refresh${p.propName}List(params);\r\n						\r\n					});\r\n					function refresh${p.propName}List(args){\r\n						args = args || {};\r\n						args.htmlType=\'yes\';\r\n						args.keyName=\'${p.propName}Keys\';\r\n						args.showCheckBox=\'false\';\r\n						args.from=\'${entity.lowerClassName}_${p.propName}\';\r\n						$.post(basePath + \"admin/${p.complexEntity.lowerClassName}/selectList.do\",args,function(d){\r\n							try{\r\n								var currentTarget = getCurrentTarget();\r\n								$(\"#${p.propName}List\",currentTarget).html(d);\r\n								$(\"table.table\", currentTarget).jTable();\r\n								initUI($(\"#${p.propName}List\",currentTarget).parent());\r\n								$(\":button.checkboxCtrl, :checkbox.checkboxCtrl\", currentTarget).checkboxCtrl(currentTarget);\r\n							}catch(e){\r\n								alert(e);\r\n							}\r\n						});\r\n					}\r\n					//-->\r\n					</script>\r\n				</div>\r\n				[_LT]!-- ${p.propName}:!-- many to many prop tab end -->\r\n		</c:if>\r\n		</c:when>\r\n	</c:choose>\r\n	\r\n</c:forEach>', null);
