INSERT INTO `t_varlabel` VALUES ('88', 'LIST_HEAD_SMART_ADMIN', '', '1', '<c:if test=\"${not empty entity.parent}\">\r\n	[_LT]%@include file=\"/WEB-INF/jsp/${entity.parent.lowerClassName}/list-head.jsp\"%[_GT]\r\n</c:if>\r\n\r\n<c:if test=\"${empty entity.parent and entity.idGenerateType==0}\">\r\n	<th  data-sortfield=\"id\" class=\"[_D]{nfn:sortClass(pageBean,\'id\')}\">[_LT]spring:message code=\"base.function.table.head.no\"/></th>\r\n</c:if>\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.display==1}\">\r\n		<c:if test=\"${p.dataType!=6 or (p.relationType != 2 and p.relationType != 3)}\">\r\n			<th align=\"center\" data-sortfield=\"${p.propName}\" class=\"[_D]{nfn:sortClass(pageBean,\'${p.propName}\')}\">\r\n			[_LT]spring:message code=\"props.${p.entity.fullClassName}.${p.propName}\"/>\r\n		</th>\r\n		</c:if>\r\n</c:if>\r\n</c:forEach>', '22');
INSERT INTO `t_varlabel` VALUES ('89', 'BODY_TDS_SMART_ADMIN', '', '1', '<c:set var=\"tempBeanName\" value=\"tempBean\"/>\r\n<c:if test=\"${not empty entity.parent}\">\r\n	[_LT]%@include file=\"/WEB-INF/jsp/${entity.parent.lowerClassName}/list-body.jsp\"%[_GT]\r\n</c:if>\r\n<c:if test=\"${empty entity.parent and entity.idGenerateType==0}\">\r\n	<td>[_D]{${tempBeanName}.${entity.idColumnName}}</td>\r\n</c:if>\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.display==1}\">\r\n	<c:choose>\r\n		<c:when test=\"${p.isAttachement==1}\">\r\n			<td>\r\n				<c:choose>\r\n					<c:when test=\"${p.isPicture==1 and p.attachementDisplayType eq \'img\'}\">\r\n						<div class=\"nailthumb-container square-thumb\" style=\"border:1px solid gray;\">\r\n							[_LT]c:choose[_GT]\r\n							[_LT]c:when test=\"[_D]{not empty tempBean.${p.propName}}\">\r\n							<a href=\"${basePath}filee/photoView.do?manageKey=[_D]{tempBean.${p.propName}.manageKey}\" target=\"_blank\" title=\"[_LT]spring:message code=\"base.function.table.content.pic.view\"/>\"><img src=\"${basePath}filee/photoView.do?manageKey=[_D]{tempBean.${p.propName}.manageKey}\" border=\"0\" /></a>\r\n							[_LT]/c:when[_GT]\r\n							[_LT]c:otherwise[_GT]\r\n							--\r\n							[_LT]/c:otherwise[_GT]\r\n							[_LT]/c:choose>\r\n						</div>\r\n					</c:when>\r\n					<c:otherwise>\r\n						<a class=\"btnDownLoad\" href=\"filee/downloadFile.do?key=[_D]{tempBean.${p.propName}.manageKey}\" target=\"dwzExport\" method=\"GET\"><span></span></a>\r\n					</c:otherwise>\r\n				</c:choose>\r\n			</td>\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==6}\">\r\n			<c:if test=\"${p.relationType != 2 and p.relationType != 3}\">\r\n			<td>[_D]{${tempBeanName}.${p.propName}<c:if test=\"${p.relationType != 2 and p.relationType != 3  and (not empty p.valuePath)}\">.${p.valuePath}</c:if>}</td>\r\n			</c:if>\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==4}\">\r\n			<td>[_LT]fmt:formatDate value=\"[_D]{${tempBeanName}.${p.