INSERT INTO `t_varlabel` VALUES ('97', 'ADD_CAUSE_ON_CRITERIA_SMART_ADMIN', '', '1', '<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n<c:if test=\"${p.relationType!=2 and p.relationType!=3}\">\r\n\r\n<c:set var=\"propAccessName\" value=\"${entity.lowerClassName}.${p.getterName}()\"/>\r\n<c:choose>\r\n    <c:when test=\"${p.dataType==5}\">  \r\n        if(${propAccessName}!=null && ${propAccessName}.getId() != null ){\r\n            criteria.add(Restrictions.eq(\"${p.propName}\",${propAccessName}));\r\n        }\r\n    </c:when>\r\n	<c:when test=\"${p.dataType==6}\">\r\n		<c:choose>\r\n			<c:when test=\"${p.select2==1}\">\r\n				if (${entity.lowerClassName}.${p.getterName}Query() != null && !${entity.lowerClassName}.${p.getterName}Query().trim().equals(\"\")) {\r\n					criteria.createAlias(\"${p.propName}\", \"${p.propName}Alias\");\r\n					criteria.add(Restrictions.like(\"${p.propName}Alias.${p.complexEntity.queryProp}\",${entity.lowerClassName}.${p.getterName}Query(),MatchMode.ANYWHERE));\r\n				}\r\n			</c:when>\r\n			<c:otherwise>\r\n				if(${propAccessName}!=null && ${propAccessName}.getId() != null ){\r\n					criteria.add(Restrictions.eq(\"${p.propName}\",${propAccessName}));\r\n				}\r\n			</c:otherwise>\r\n		</c:choose>\r\n	</c:when>\r\n    <c:when test=\"${p.forQuery==1 and (p.dataType==4 or dataType==3 or dataType==2)}\">\r\nif(${entity.lowerClassName}.${p.getterName}Start()!=null){\r\ncriteria.add(Restrictions.ge(\"${p.propName}\",${entity.lowerClassName}.${p.getterName}Start()));\r\n}\r\nif(${entity.lowerClassName}.${p.getterName}End()!=null){\r\ncriteria.add(Restrictions.le(\"${p.propName}\",${entity.lowerClassName}.${p.getterName}End()));\r\n}\r\n    </c:when>\r\n    <c:otherwise>\r\n       if(${p.entity.lowerClassName}.${p.getterName}()!=null\r\n<c:if test=\"${p.dataType==1}\"> && ! ${p.entity.lowerClassName}.${p.getterName}().trim().equals(\"\")</c:if>){\r\n		criteria.add(Restrictions.${p.dataType==1?\'like\':\'eq\'}(\"${p.propName}\",${entity.lowerClassName}.${p.getterName}()${p.dataType==1?\',MatchMode.ANYWHERE\':\'\'}));\r\n}\r\n    </c:otherwise>\r\n</c:choose>\r\n\r\n</c:if>\r\n</c:forEach>', '22');
