INSERT INTO `t_varlabel` VALUES ('108', 'X_TO_MANY_DETAIL_FORM_SMART_ADMIN', '', '1', '<c:if test=\"${not empty entity.parent}\">\r\n	[_LT]%@include file=\"/WEB-INF/jsp/${entity.parent.lowerClassName}/detail-x2many-form.jsp\"%[_GT]\r\n</c:if>\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n	<c:choose>\r\n		<c:when test=\"${p.dataType==6 and p.relationType==3}\">\r\n			\r\n			<c:if test=\"${p.maintainOneToManyRelation eq 1}\">\r\n				\r\n				<c:if test=\"${p.complexEntity.historyable!=1}\">\r\n				[_LT]c:if test=\"[_D]{not historyView}\">\r\n				</c:if>\r\n				[_LT]!-- ${p.name} ${p.propName}:one to many prop tab start -->\r\n				<div class=\"tab-pane fade tab-form\" id=\"${p.propName}\" \r\n				data-formurl=\"[_D]{basePath}${p.complexEntity.lowerClassName}/tabDetailForm.do\"\r\n				data-initdata=\"[_D]{nfn:serialKyes(tempBean.${p.propName},\'${p.complexEntity.manageId}\')}\"\r\n				data-trtarget=\"${p.propName}\"\r\n				>\r\n					\r\n					<input type=\"hidden\" name=\"${p.propName}TrCount\" value=\"0\"/>\r\n					<div class=\"dataTables_wrapper\" style=\"border-bottom:1px solid #ddd;\"> \r\n						<table id=\"dt_basic\" class=\"table table-striped table-bordered table-hover tab-form-table\"\r\n							   data-trindex=\"[_D]{fn:length(tempBean.${p.propName}) - 1}\"\r\n							   >\r\n							<thead>\r\n								<tr>\r\n									[_LT]%@include file=\"/WEB-INF/jsp/${p.complexEntity.lowerClassName}/tab-add-list-head.jsp\" %[_GT]\r\n								</tr>\r\n							</thead>\r\n							<tbody>\r\n								\r\n								\r\n							</tbody>\r\n						</table>\r\n					\r\n					</div>\r\n				</div>\r\n				[_LT]!-- ${p.name} ${p.propName}:!-- one to many prop tab end -->\r\n				<c:if test=\"${p.complexEntity.historyable!=1}\">\r\n				[_LT]/c:if>\r\n				</c:if>\r\n				\r\n				\r\n			</c:if>\r\n			\r\n		</c:when>\r\n		<c:when test=\"${p.dataType==6 and p.relationType==2}\">\r\n			<c:if test=\"${p.maintainOneToManyRelation eq 1}\">\r\n				\r\n				<c:if test=\"${p.complexEntity.historyable