INSERT INTO `t_varlabel` VALUES ('109', 'TAB_DETAIL_FORM_INPUT_SMART_ADMIN', '', '1', '<c:set var=\"tempBeanName\" value=\"tempBean\"/>\r\n<c:if test=\"${not empty entity.parent}\">\r\n	[_LT]%@include file=\"/WEB-INF/jsp/${entity.parent.lowerClassName}/tab-detail-form.jsp\"%>\r\n</c:if>\r\n<c:if test=\"${empty entity.parent}\">\r\n	[_LT]%@include file=\"/WEB-INF/jsp/common/taglib.jsp\"%>\r\n</c:if>\r\n\r\n\r\n\r\n<c:forEach items=\"${entity.properties}\" var=\"p\">\r\n	<c:if test=\"${p.onlyRelationship!=1 and (empty p.noPersistence or p.noPersistence==0)}\">\r\n		<c:if test=\"${not (p.dataType==6 and (p.relationType==2 or p.relationType==3))}\">\r\n		\r\n				<td>\r\n						<c:choose>\r\n							<c:when test=\"${p.isAttachement==1}\">\r\n								\r\n								<c:choose>\r\n									<c:when test=\"${p.isPicture==1 and p.attachementDisplayType eq \'img\'}\">\r\n										<div class=\"nailthumb-container square-thumb\" style=\"border:1px solid gray;\">\r\n											[_LT]c:choose[_GT]\r\n											[_LT]c:when test=\"[_D]{not empty tempBean.${p.propName}}\">\r\n											<a href=\"${basePath}filee/photoView.do?manageKey=[_D]{tempBean.${p.propName}.manageKey}\" target=\"_blank\" title=\"[_LT]spring:message code=\"base.function.table.content.pic.view\"/>\"><img src=\"${basePath}filee/photoView.do?manageKey=[_D]{tempBean.${p.propName}.manageKey}\" border=\"0\" /></a>\r\n											[_LT]/c:when[_GT]\r\n											[_LT]c:otherwise[_GT]\r\n											--\r\n											[_LT]/c:otherwise[_GT]\r\n											[_LT]/c:choose>\r\n										</div>\r\n									</c:when>\r\n									<c:otherwise>\r\n										<a class=\"btnDownLoad\" href=\"filee/downloadFile.do?key=[_D]{tempBean.${p.propName}.manageKey}\" target=\"dwzExport\" method=\"GET\"><span></span></a>\r\n									</c:otherwise>\r\n								</c:choose>\r\n								\r\n							</c:when>\r\n							<c:when test=\"${p.dataType==6}\">\r\n								<c:if test=\"${p.relationType != 2 and p.relationType != 3}\">\r\n									[_D]{${tempBeanName}.